function calculate({ current_ph, target_ph, soil_type, bed_length, bed_width, adj_depth }) {
  const area_sq_ft = bed_length * bed_width;
  const min_area_sq_ft = 1; // Minimum area to avoid division by zero or nonsensical results
  if (area_sq_ft < min_area_sq_ft) {
    return { lime_needed_lbs: 0, sulfur_needed_lbs: 0 };
  }

  const ph_diff = target_ph - current_ph;
  const abs_ph_diff = Math.abs(ph_diff);

  if (abs_ph_diff < 0.1) { // If difference is negligible, no adjustment needed
    return { lime_needed_lbs: 0, sulfur_needed_lbs: 0 };
  }

  // Normalize adjustment depth to a standard 6 inches for base rates
  const depth_factor = Math.max(0.1, adj_depth / 6); // Ensure depth factor is not zero or negative

  let lime_rate_per_100sqft_per_ph = 0;
  let sulfur_rate_per_100sqft_per_ph = 0;

  const normalized_soil_type = soil_type.toLowerCase();

  switch (normalized_soil_type) {
    case 'sandy':
      lime_rate_per_100sqft_per_ph = 5;   // lbs of calcitic lime
      sulfur_rate_per_100sqft_per_ph = 0.8; // lbs of elemental sulfur
      break;
    case 'loamy':
    case 'silt': // often grouped with loamy for general guidance
      lime_rate_per_100sqft_per_ph = 8;   // lbs of calcitic lime
      sulfur_rate_per_100sqft_per_ph = 1.5; // lbs of elemental sulfur
      break;
    case 'clay':
      lime_rate_per_100sqft_per_ph = 12;  // lbs of calcitic lime
      sulfur_rate_per_100sqft_per_ph = 2.5; // lbs of elemental sulfur
      break;
    default:
      // Default to loamy if soil type is unrecognized, as a reasonable average
      lime_rate_per_100sqft_per_ph = 8;   
      sulfur_rate_per_100sqft_per_ph = 1.5; 
  }

  let lime_needed_lbs = 0;
  let sulfur_needed_lbs = 0;

  const min_output_lbs = 0.01; // Minimum output to show a non-zero value

  if (ph_diff > 0) { // Need to raise pH (add lime)
    lime_needed_lbs = (ph_diff * lime_rate_per_100sqft_per_ph * (area_sq_ft / 100) * depth_factor);
    lime_needed_lbs = Math.max(0, lime_needed_lbs); // Ensure non-negative
    // Cap max lime to prevent extreme, potentially harmful recommendations (e.g., ~20 lbs per 100 sq ft max in a single application)
    lime_needed_lbs = Math.min(lime_needed_lbs, 20 * (area_sq_ft / 100)); 
  } else if (ph_diff < 0) { // Need to lower pH (add sulfur)
    sulfur_needed_lbs = (abs_ph_diff * sulfur_rate_per_100sqft_per_ph * (area_sq_ft / 100) * depth_factor);
    sulfur_needed_lbs = Math.max(0, sulfur_needed_lbs); // Ensure non-negative
    // Cap max sulfur similarly (e.g., ~3 lbs per 100 sq ft max in a single application)
    sulfur_needed_lbs = Math.min(sulfur_needed_lbs, 3 * (area_sq_ft / 100)); 
  }

  return {
    lime_needed_lbs: lime_needed_lbs < min_output_lbs ? 0 : lime_needed_lbs,
    sulfur_needed_lbs: sulfur_needed_lbs < min_output_lbs ? 0 : sulfur_needed_lbs
  };
}
