function calculateCosts(inputs) {
    const {
        annual_deductible,
        insurance_coverage_percentage,
        annual_out_of_pocket_maximum,
        weekly_therapy_sessions,
        cost_per_therapy_session,
        monthly_hormone_cost,
        major_surgery_cost_estimate,
        years_of_planning_horizon
    } = inputs;

    const co_insurance_factor = (100 - insurance_coverage_percentage) / 100;

    // --- Year 1 Calculation (includes surgery) ---
    const annual_therapy_cost_year1 = weekly_therapy_sessions * 52 * cost_per_therapy_session;
    const annual_hormone_cost_year1 = monthly_hormone_cost * 12;
    const total_medical_expenses_year1 = annual_therapy_cost_year1 + annual_hormone_cost_year1 + major_surgery_cost_estimate;

    let year1_oop_before_cap;

    if (total_medical_expenses_year1 <= annual_deductible) {
        year1_oop_before_cap = total_medical_expenses_year1;
    } else {
        year1_oop_before_cap = annual_deductible + ((total_medical_expenses_year1 - annual_deductible) * co_insurance_factor);
    }

    let total_oop_year1 = Math.min(year1_oop_before_cap, annual_out_of_pocket_maximum);
    let insurance_contribution_year1 = total_medical_expenses_year1 - total_oop_year1;

    // --- Subsequent Years Calculation (no major surgery) ---
    const annual_therapy_cost_subsequent = weekly_therapy_sessions * 52 * cost_per_therapy_session;
    const annual_hormone_cost_subsequent = monthly_hormone_cost * 12;
    const total_medical_expenses_subsequent_annual = annual_therapy_cost_subsequent + annual_hormone_cost_subsequent;

    let subsequent_oop_before_cap;

    if (total_medical_expenses_subsequent_annual <= annual_deductible) {
        subsequent_oop_before_cap = total_medical_expenses_subsequent_annual;
    } else {
        subsequent_oop_before_cap = annual_deductible + ((total_medical_expenses_subsequent_annual - annual_deductible) * co_insurance_factor);
    }

    let total_oop_subsequent_years_annual = Math.min(subsequent_oop_before_cap, annual_out_of_pocket_maximum);
    let insurance_contribution_subsequent_years = total_medical_expenses_subsequent_annual - total_oop_subsequent_years_annual;

    // --- Overall Plan Calculation ---
    let total_oop_overall = total_oop_year1;
    let total_insurance_contribution_overall = insurance_contribution_year1;

    if (years_of_planning_horizon > 1) {
        total_oop_overall += (total_oop_subsequent_years_annual * (years_of_planning_horizon - 1));
        total_insurance_contribution_overall += (insurance_contribution_subsequent_years * (years_of_planning_horizon - 1));
    }

    // Ensure negative values are not returned for OOP or insurance contributions
    total_oop_year1 = Math.max(0, total_oop_year1);
    total_oop_subsequent_years_annual = Math.max(0, total_oop_subsequent_years_annual);
    total_oop_overall = Math.max(0, total_oop_overall);
    total_insurance_contribution_overall = Math.max(0, total_insurance_contribution_overall);

    return {
        total_oop_year1: total_oop_year1,
        total_oop_subsequent_years_annual: total_oop_subsequent_years_annual,
        total_oop_overall: total_oop_overall,
        total_insurance_contribution: total_insurance_contribution_overall
    };
}
