function calculateRisk(inputs) {
    const { 
        sourceCountryStabilityScore, 
        sourceCountryCorruptionIndex, 
        importVolumeDailyBarrels, 
        marketPricePerBarrel, 
        tradeDependencyPercentage, 
        shippingRouteRiskScore, 
        regionalConflictPotentialScore, 
        alternativeSupplyAvailabilityDays, 
        infrastructureVulnerabilityScore 
    } = inputs;

    // Input validation and clamping
    const stability = Math.max(1, Math.min(10, sourceCountryStabilityScore));
    const corruption = Math.max(0, Math.min(100, sourceCountryCorruptionIndex));
    const volume = Math.max(0, importVolumeDailyBarrels);
    const price = Math.max(0.01, marketPricePerBarrel);
    const dependency = Math.max(0, Math.min(100, tradeDependencyPercentage));
    const routeRisk = Math.max(1, Math.min(10, shippingRouteRiskScore));
    const conflict = Math.max(1, Math.min(10, regionalConflictPotentialScore));
    const buffer = Math.max(0, alternativeSupplyAvailabilityDays);
    const infraVulnerability = Math.max(1, Math.min(10, infrastructureVulnerabilityScore));

    // --- Step 1: Calculate Geopolitical Instability Index (GII) ---
    // Lower stability and corruption (higher index) means higher instability.
    // Stability is inverse: (10 - stability) / 9 to scale 1-10 to 0-1. Max instability at stability=1, min at stability=10
    // Corruption is inverse: (100 - corruption) / 100 to scale 0-100 to 0-1. Max corruption at corruption=0, min at corruption=100
    const stabilityFactor = (10 - stability) / 9; // Scales 0 (stable) to 1 (unstable)
    const corruptionFactor = (100 - corruption) / 100; // Scales 0 (least corrupt) to 1 (most corrupt)
    const gii = (stabilityFactor * 0.4 + corruptionFactor * 0.3 + (conflict / 10) * 0.3) * 10; // Weighted average, scaled to 0-10

    // --- Step 2: Calculate Supply Chain Vulnerability Index (SCVI) ---
    // Higher route risk, infra vulnerability, and dependency means higher vulnerability.
    const scvi = (((routeRisk / 10) * 0.4) + ((infraVulnerability / 10) * 0.3) + ((dependency / 100) * 0.3)) * 10; // Weighted average, scaled to 0-10

    // --- Step 3: Calculate Resilience Factor (RF) ---
    // Higher buffer reduces risk, scaled inversely.
    const bufferInfluence = Math.min(1, buffer / 180); // Capping buffer influence at 180 days, where it provides max resilience. Scales 0 (no buffer) to 1 (high buffer)
    const rf = (1 - bufferInfluence) * 10; // Resilience factor is inverse of buffer influence. 0 (highly resilient) to 10 (not resilient)

    // --- Step 4: Combine indices for Overall Risk Score (ORS) ---
    // ORS heavily influenced by GII, SCVI, and adjusted by RF.
    const baseRisk = (gii * 0.4 + scvi * 0.4 + rf * 0.2); // Weighted sum for base risk 0-10
    const overallRiskScore = Math.min(100, Math.max(0, baseRisk * 10)); // Scale to 0-100

    // --- Step 5: Estimate Operational Disruption Potential ---
    // A higher ORS means higher potential disruption. Max disruption cap at 90 days for plausible modeling.
    // Operational disruption is influenced by the overall risk, but also by dependency and infrastructure vulnerability.
    // Scale ORS to a potential disruption duration.
    const maxDisruptionDays = 90; // Max reasonable disruption in a year due to these factors
    const operationalDisruptionPotentialDays = Math.min(maxDisruptionDays, (overallRiskScore / 100) * maxDisruptionDays * (1 + (dependency/100) * 0.5 + (infraVulnerability/10)*0.2));
    // Adjust based on alternative supply buffer - reduces potential disruption
    const adjustedDisruption = Math.max(0, operationalDisruptionPotentialDays - buffer);

    // Ensure operationalDisruptionPotentialDays is not negative if buffer is very high
    const finalOperationalDisruptionPotentialDays = Math.max(0, Math.min(maxDisruptionDays, adjustedDisruption));

    // --- Step 6: Estimate Financial Impact ---
    // Financial impact is disruption days * daily volume * market price.
    const dailyValue = volume * price;
    const financialImpactAnnualUSD = finalOperationalDisruptionPotentialDays * dailyValue;

    // --- Step 7: Determine Risk Mitigation Tier ---
    let riskMitigationTier = "";
    if (overallRiskScore < 25) {
        riskMitigationTier = "Tier 1: Monitor & Optimize (Low Risk)";
    } else if (overallRiskScore < 50) {
        riskMitigationTier = "Tier 2: Contingency Planning (Moderate Risk)";
    } else if (overallRiskScore < 75) {
        riskMitigationTier = "Tier 3: Strategic Diversification & Hedging (High Risk)";
    } else {
        riskMitigationTier = "Tier 4: Urgent Re-evaluation & De-risking (Critical Risk)";
    }

    return {
        overallRiskScore: overallRiskScore,
        operationalDisruptionPotentialDays: finalOperationalDisruptionPotentialDays,
        financialImpactAnnualUSD: financialImpactAnnualUSD,
        riskMitigationTier: riskMitigationTier
    };
}
