function calculate(inputs) {
  const producerRegionInstabilityIndex = Math.max(0, Math.min(10, inputs.producerRegionInstabilityIndex));
  const resourceNationalismThreat = Math.max(0, Math.min(10, inputs.resourceNationalismThreat));
  const sanctionsExposureScore = Math.max(0, Math.min(10, inputs.sanctionsExposureScore));
  const supplyChainConcentrationIndex = Math.max(0, Math.min(10, inputs.supplyChainConcentrationIndex));
  const logisticsVulnerabilityIndex = Math.max(0, Math.min(10, inputs.logisticsVulnerabilityIndex));
  const orgDependencyOnPrimarySource = Math.max(0, Math.min(100, inputs.orgDependencyOnPrimarySource));
  const supplyDiversificationScore = Math.max(0, Math.min(10, inputs.supplyDiversificationScore));

  // Step 1: Calculate core component risks (scaled 0-100)
  const geopoliticalInstabilityRisk = (producerRegionInstabilityIndex * 0.6 + resourceNationalismThreat * 0.4) * 10;
  const economicSanctionsRisk = sanctionsExposureScore * 10;
  const supplyChainVulnerabilityRisk = (supplyChainConcentrationIndex * 0.7 + logisticsVulnerabilityIndex * 0.3) * 10;

  // Step 2: Combine component risks into an initial source-level risk
  const combinedSourceRisk = (
    geopoliticalInstabilityRisk * 0.45 + 
    economicSanctionsRisk * 0.35 + 
    supplyChainVulnerabilityRisk * 0.20
  );

  // Step 3: Adjust for organizational dependency on the primary at-risk source
  const orgDependencyFactor = orgDependencyOnPrimarySource / 100;
  // Higher dependency linearly amplifies the risk. If dependency is 0, base risk is 0.
  const riskAdjustedForDependency = combinedSourceRisk * orgDependencyFactor;

  // Step 4: Mitigate the risk based on supply diversification effectiveness
  // A higher diversification score reduces the risk more. Score of 10 means significant reduction.
  const diversificationReductionFactor = supplyDiversificationScore / 10; // Converts 0-10 score to 0-1 reduction factor

  // Apply a minimum residual risk (e.g., 10% of riskAdjustedForDependency) even with perfect diversification, but cap it at 0.
  const overallRiskScoreUncapped = riskAdjustedForDependency * (1 - diversificationReductionFactor);

  // Ensure overall risk is between 0 and 100
  const overallRiskScore = Math.max(0, Math.min(100, overallRiskScoreUncapped));

  return {
    overallRiskScore: overallRiskScore,
    geopoliticalInstabilityRisk: Math.max(0, Math.min(100, geopoliticalInstabilityRisk)),
    economicSanctionsRisk: Math.max(0, Math.min(100, economicSanctionsRisk))
  };
}
