function calculate({
    eventSeverity,
    politicalStabilityImpact,
    policyPredictabilityImpact,
    internationalRelationsImpact,
    investmentExposurePercentage,
    sectorSensitivityScore,
    regionalEconomicResilienceScore,
    investmentTimeHorizonYears
}) {
    // Input validation and clamping to ensure values are within expected ranges
    eventSeverity = Math.max(1, Math.min(10, eventSeverity || 1));
    politicalStabilityImpact = Math.max(1, Math.min(10, politicalStabilityImpact || 1));
    policyPredictabilityImpact = Math.max(1, Math.min(10, policyPredictabilityImpact || 1));
    internationalRelationsImpact = Math.max(1, Math.min(10, internationalRelationsImpact || 1));
    investmentExposurePercentage = Math.max(0, Math.min(100, investmentExposurePercentage || 0));
    sectorSensitivityScore = Math.max(1, Math.min(10, sectorSensitivityScore || 1));
    regionalEconomicResilienceScore = Math.max(1, Math.min(10, regionalEconomicResilienceScore || 1));
    investmentTimeHorizonYears = Math.max(0, investmentTimeHorizonYears || 0);

    // Step 1: Calculate the Geopolitical Event Impact Score (GEIS)
    // Weights are assigned based on perceived importance: Political Stability and Policy Predictability are highly critical.
    const geisWeightSum = 1.5 + 2.0 + 1.8 + 1.7;
    const geopoliticalEventImpactScore = (
        (eventSeverity * 1.5) +
        (politicalStabilityImpact * 2.0) +
        (policyPredictabilityImpact * 1.8) +
        (internationalRelationsImpact * 1.7)
    ) / geisWeightSum; // Resulting score is out of 10

    // Step 2: Calculate the Investment Vulnerability Score (IVS)
    // Regional resilience is inverted to represent risk (10=very resilient => effectively 1 for risk factor; 1=low resilience => effectively 10 for risk factor).
    // Investment time horizon contributes to sustained risk, with a cap (e.g., 20 years) to avoid excessive impact from very long horizons.
    const effectiveRegionalResilienceRisk = 11 - regionalEconomicResilienceScore; // Transforms 1-10 resilience to 10-1 risk factor
    const timeHorizonRiskFactor = Math.min(investmentTimeHorizonYears, 20) / 20 * 10; // Max 10 points for time risk, saturating at 20 years

    const ivsWeightSum = 1.2 + 1.0 + 0.8;
    const investmentVulnerabilityScore = (
        (sectorSensitivityScore * 1.2) +
        (effectiveRegionalResilienceRisk * 1.0) +
        (timeHorizonRiskFactor * 0.8)
    ) / ivsWeightSum; // Resulting score is out of 10

    // Step 3: Combine scores to get Raw Geopolitical Risk Score
    // The raw score is a product to reflect compounding risk, normalized to 100.
    const rawGeopoliticalRisk = (geopoliticalEventImpactScore * investmentVulnerabilityScore); // Max 10 * 10 = 100

    // Step 4: Calculate the final Geopolitical Investment Risk Index
    // This incorporates the percentage of the investment directly exposed to the event, scaling the raw risk.
    const geopoliticalRiskIndex = (rawGeopoliticalRisk / 100) * investmentExposurePercentage; // Final index is out of 100

    // Step 5: Determine Risk Categorization based on the final index
    let riskCategorization;
    if (geopoliticalRiskIndex < 25) {
        riskCategorization = "Low Risk";
    }
    else if (geopoliticalRiskIndex < 50) {
        riskCategorization = "Moderate Risk";
    }
    else if (geopoliticalRiskIndex < 75) {
        riskCategorization = "High Risk";
    }
    else {
        riskCategorization = "Critical Risk";
    }

    // Step 6: Estimate Projected Capital At Risk as a percentage of TOTAL investment value
    // This is a non-linear estimate: higher risk indices imply a disproportionately higher potential loss percentage.
    // The formula (GPR/100) * (20 + (GPR/100)*80) means:
    // GPR=0 -> 0% at risk
    // GPR=50 -> 0.5 * (20 + 0.5*80) = 0.5 * (20 + 40) = 0.5 * 60 = 30% at risk
    // GPR=100 -> 1 * (20 + 1*80) = 1 * 100 = 100% at risk
    const baseCapitalAtRiskPercentage = (geopoliticalRiskIndex / 100) * (20 + (geopoliticalRiskIndex / 100) * 80); 
    const projectedCapitalAtRisk = baseCapitalAtRiskPercentage; // This represents the estimated % of total capital that is potentially at risk.

    return {
        geopoliticalRiskIndex: geopoliticalRiskIndex,
        riskCategorization: riskCategorization,
        projectedCapitalAtRisk: projectedCapitalAtRisk
    };
}
