const clamp = (value, min, max) => Math.max(min, Math.min(value, max));

const initialReturn = inputs.initialInvestmentReturn;
const polStab = clamp(inputs.politicalStabilityScore, 1, 10);
const intRel = clamp(inputs.internationalRelationsScore, 1, 10);
const policyPred = clamp(inputs.policyPredictabilityScore, 1, 10);
const economicVul = clamp(inputs.economicVulnerabilityFactor, 0.5, 2.0);
const regEnv = clamp(inputs.regulatoryEnvironmentScore, 1, 10);
const regConflict = clamp(inputs.regionalConflictPotential, 1, 10);
const corruption = clamp(inputs.corruptionPerceptionScore, 1, 10);

const W_POL_STAB = 0.20;
const W_INT_REL = 0.15;
const W_POLICY_PRED = 0.20;
const W_REG_ENV = 0.10;
const W_REG_CONFLICT = 0.20;
const W_CORRUPTION = 0.15;
const TOTAL_WEIGHTS = W_POL_STAB + W_INT_REL + W_POLICY_PRED + W_REG_ENV + W_REG_CONFLICT + W_CORRUPTION;

const weightedAverageRiskScore = (
    (polStab * W_POL_STAB) +
    (intRel * W_INT_REL) +
    (policyPred * W_POLICY_PRED) +
    (regEnv * W_REG_ENV) +
    (regConflict * W_REG_CONFLICT) +
    (corruption * W_CORRUPTION)
) / TOTAL_WEIGHTS;

const geopoliticalRiskScore = weightedAverageRiskScore * economicVul;

const MIN_GEOPOLITICAL_RISK_SCORE = 0.5; // (1 * 1.0 weighted avg) * 0.5 (min economic vul)
const MAX_GEOPOLITICAL_RISK_SCORE = 20.0; // (10 * 1.0 weighted avg) * 2.0 (max economic vul)

const normalizedRisk = (geopoliticalRiskScore - MIN_GEOPOLITICAL_RISK_SCORE) /
                       (MAX_GEOPOLITICAL_RISK_SCORE - MIN_GEOPOLITICAL_RISK_SCORE);
const clampedNormalizedRisk = clamp(normalizedRisk, 0, 1);

const MAX_RETURN_REDUCTION_PERCENTAGE = 0.80;
const riskAdjustmentFactor = 1 - (clampedNormalizedRisk * MAX_RETURN_REDUCTION_PERCENTAGE);

const adjustedInvestmentReturn = initialReturn * riskAdjustmentFactor;

return {
    adjustedInvestmentReturn: adjustedInvestmentReturn,
    riskAdjustmentFactor: riskAdjustmentFactor,
    geopoliticalRiskScore: geopoliticalRiskScore
};
