const currentPortfolioValue = inputs.currentPortfolioValue;
const portfolioRiskExposure = inputs.portfolioRiskExposure;
const geopoliticalRiskMagnitude = inputs.geopoliticalRiskMagnitude;
const portfolioInternationalExposurePercent = inputs.portfolioInternationalExposurePercent;
const historicalAnnualVolatilityPercent = inputs.historicalAnnualVolatilityPercent;
const riskEventDurationMonths = inputs.riskEventDurationMonths;
const marketSensitivityBeta = inputs.marketSensitivityBeta;

const historicalAnnualVolatilityDecimal = historicalAnnualVolatilityPercent / 100;
const portfolioInternationalExposureDecimal = portfolioInternationalExposurePercent / 100;

// Step 1: Calculate Base Risk Score (BRS)
// Higher geopolitical magnitude and higher portfolio risk exposure contribute more.
// Scale geopoliticalRiskMagnitude from 1-10 to 0.1-1.0
// Scale portfolioRiskExposure from 1-5 to 0.05-0.25
const geopoliticalWeight = geopoliticalRiskMagnitude * 0.1; // Max 1.0
const portfolioRiskWeight = portfolioRiskExposure * 0.05;   // Max 0.25
const baseRiskScore = geopoliticalWeight + portfolioRiskWeight; // Range approx 0.15 to 1.25

// Step 2: Calculate Exposure Multiplier (EM)
// International exposure and market sensitivity amplify the risk.
// 1 + (international exposure % * 0.75) - assuming international exposure heightens sensitivity by up to 75%
// marketSensitivityBeta directly multiplies the impact
const exposureMultiplier = (1 + portfolioInternationalExposureDecimal * 0.75) * marketSensitivityBeta;

// Step 3: Calculate Duration Impact Factor (DIF)
// Impact grows non-linearly with duration, but with diminishing returns, capped at a certain point.
const riskDurationYears = riskEventDurationMonths / 12;
const durationImpactFactor = Math.min(2.0, Math.pow(riskDurationYears, 0.7)); // Max 2.0x for long durations

// Step 4: Calculate Total Impact Multiplier (TIM)
// This multiplier will determine the overall severity before applying to value/volatility.
const totalImpactMultiplier = baseRiskScore * exposureMultiplier * durationImpactFactor;

// --- Outputs Calculation ---

// Output 1: Potential Portfolio Value Impact ($)
// The value impact is proportional to current portfolio value, historical volatility, and the total impact multiplier.
// A higher TIM implies a larger percentage impact on value. Scaled by 0.5 to keep impact reasonable, as TIM can vary significantly.
let potentialValueImpact = currentPortfolioValue * historicalAnnualVolatilityDecimal * totalImpactMultiplier * 0.5;
potentialValueImpact = Math.min(potentialValueImpact, currentPortfolioValue * 0.5); // Cap at 50% of portfolio value
potentialValueImpact = Math.max(0, potentialValueImpact); // Ensure non-negative

// Output 2: Estimated Annual Volatility Increase (percentage points)
// The increase in volatility points is driven by TIM and historical volatility.
// Scaled by 0.3 to get a reasonable point increase, capped at 25 additional percentage points.
let estimatedVolatilityIncreasePoints = totalImpactMultiplier * historicalAnnualVolatilityDecimal * 100 * 0.3;
estimatedVolatilityIncreasePoints = Math.min(estimatedVolatilityIncreasePoints, 25); // Cap at 25 additional percentage points
estimatedVolatilityIncreasePoints = Math.max(0, estimatedVolatilityIncreasePoints); // Ensure non-negative

return {
    potentialValueImpact: potentialValueImpact,
    estimatedVolatilityIncreasePercent: estimatedVolatilityIncreasePoints,
};
