/**
 * Calculates the Geopolitical Investment Risk Premium based on various input factors.
 * Returns an object with 'geopoliticalRiskFactor', 'additionalGeopoliticalRiskPremium', and 'totalAdjustedRiskPremium'.
 *
 * @param {object} inputs - The input values from the calculator UI.
 * @param {number} inputs.politicalStabilityIndex - Score from 0 (unstable) to 10 (stable).
 * @param {number} inputs.sanctionsRiskScore - Score from 0 (no risk) to 10 (extreme risk).
 * @param {number} inputs.supplyChainVulnerabilityScore - Score from 0 (resilient) to 10 (highly vulnerable).
 * @param {number} inputs.regionalConflictIntensity - Score from 0 (peaceful) to 10 (active conflict).
 * @param {number} inputs.resourceDependenceLevel - Percentage from 0 to 100.
 * @param {number} inputs.governanceEffectivenessScore - Score from 0 (poor) to 10 (effective).
 * @param {number} inputs.baseMarketRiskPremium - Existing risk premium in percentage.
 *
 * @returns {object} An object containing the calculated outputs.
 */
function calculateGeopoliticalRiskPremium(inputs) {
  // Input validation and clamping to ensure values are within expected ranges
  const politicalStabilityIndex = Math.max(0, Math.min(10, inputs.politicalStabilityIndex || 0));
  const sanctionsRiskScore = Math.max(0, Math.min(10, inputs.sanctionsRiskScore || 0));
  const supplyChainVulnerabilityScore = Math.max(0, Math.min(10, inputs.supplyChainVulnerabilityScore || 0));
  const regionalConflictIntensity = Math.max(0, Math.min(10, inputs.regionalConflictIntensity || 0));
  const resourceDependenceLevel = Math.max(0, Math.min(100, inputs.resourceDependenceLevel || 0));
  const governanceEffectivenessScore = Math.max(0, Math.min(10, inputs.governanceEffectivenessScore || 0));
  const baseMarketRiskPremium = Math.max(0.1, Math.min(15.0, inputs.baseMarketRiskPremium || 0.1)); // Ensure a minimum base premium

  // Step 1: Normalize Input Scores to Risk Contributions (0-1)
  // Higher risk contribution means higher risk. Index scores are inverted where higher score means better.
  const psiRiskContribution = (10 - politicalStabilityIndex) / 10; // 0 (stable) -> 1 (unstable)
  const srsRiskContribution = sanctionsRiskScore / 10;           // 0 (no risk) -> 1 (extreme risk)
  const scvRiskContribution = supplyChainVulnerabilityScore / 10; // 0 (resilient) -> 1 (vulnerable)
  const rciRiskContribution = regionalConflictIntensity / 10;     // 0 (peaceful) -> 1 (conflict)
  const rdlRiskContribution = resourceDependenceLevel / 100;      // 0 (no dependence) -> 1 (high dependence)
  const gesRiskContribution = (10 - governanceEffectivenessScore) / 10; // 0 (effective) -> 1 (ineffective)

  // Step 2: Assign Weights and Calculate Weighted Geopolitical Risk Factor
  // These weights can be adjusted based on expert judgment or empirical analysis.
  const WEIGHT_PSI = 0.20;
  const WEIGHT_SRS = 0.25;
  const WEIGHT_SCV = 0.15;
  const WEIGHT_RCI = 0.20;
  const WEIGHT_RDL = 0.10;
  const WEIGHT_GES = 0.10;
  // Total weight check: sum should be 1.

  let geopoliticalRiskFactor = (
    (psiRiskContribution * WEIGHT_PSI) +
    (srsRiskContribution * WEIGHT_SRS) +
    (scvRiskContribution * WEIGHT_SCV) +
    (rciRiskContribution * WEIGHT_RCI) +
    (rdlRiskContribution * WEIGHT_RDL) +
    (gesRiskContribution * WEIGHT_GES)
  );

  // Clamp the geopoliticalRiskFactor between 0 and 1
  geopoliticalRiskFactor = Math.max(0, Math.min(1, geopoliticalRiskFactor));

  // Step 3: Determine Maximum Potential Additional Premium
  // This represents the maximum percentage points that can be added to the base premium
  // when all geopolitical risk factors are at their highest (geopoliticalRiskFactor = 1).
  const MAX_ADDITIONAL_PREMIUM_POINTS = 12.0; // e.g., an extremely risky scenario adds up to 12% points
  const MIN_ADDITIONAL_PREMIUM_POINTS = 0.0; // The minimum additional premium is 0%

  // Step 4: Calculate Additional Geopolitical Risk Premium
  // This is a dynamic percentage addition based on the calculated geopoliticalRiskFactor.
  let additionalGeopoliticalRiskPremium = geopoliticalRiskFactor * MAX_ADDITIONAL_PREMIUM_POINTS;

  // Cap the additional premium to prevent unrealistic inflation, e.g., maximum 15% added.
  additionalGeopoliticalRiskPremium = Math.max(MIN_ADDITIONAL_PREMIUM_POINTS, Math.min(15.0, additionalGeopoliticalRiskPremium));

  // Step 5: Calculate Total Adjusted Risk Premium
  let totalAdjustedRiskPremium = baseMarketRiskPremium + additionalGeopoliticalRiskPremium;

  // Introduce an overall cap for the total adjusted risk premium to prevent extreme outliers,
  // as certain investments may simply become uninvestable beyond a certain risk threshold.
  const OVERALL_TOTAL_PREMIUM_CAP = 30.0; // e.g., a total risk premium cannot exceed 30%
  totalAdjustedRiskPremium = Math.min(OVERALL_TOTAL_PREMIUM_CAP, totalAdjustedRiskPremium);

  return {
    geopoliticalRiskFactor: geopoliticalRiskFactor,
    additionalGeopoliticalRiskPremium: additionalGeopoliticalRiskPremium,
    totalAdjustedRiskPremium: totalAdjustedRiskPremium
  };
}
