function calculate() {
    const politicalStabilityScore = parseFloat(inputs.politicalStabilityScore);
    const regulatoryRiskScore = parseFloat(inputs.regulatoryRiskScore);
    const tradePolicyVolatilityScore = parseFloat(inputs.tradePolicyVolatilityScore);
    const regionalConflictIntensity = parseFloat(inputs.regionalConflictIntensity);
    const sanctionsRiskScore = parseFloat(inputs.sanctionsRiskScore);
    const governanceEffectivenessScore = parseFloat(inputs.governanceEffectivenessScore);
    const socioEconomicUnrestPotential = parseFloat(inputs.socioEconomicUnrestPotential);
    const ruleOfLawScore = parseFloat(inputs.ruleOfLawScore);

    // Input validation: Ensure all scores are within 1-10
    const scores = [
        politicalStabilityScore, regulatoryRiskScore, tradePolicyVolatilityScore,
        regionalConflictIntensity, sanctionsRiskScore, governanceEffectivenessScore,
        socioEconomicUnrestPotential, ruleOfLawScore
    ];
    for (const score of scores) {
        if (isNaN(score) || score < 1 || score > 10) {
            throw new Error('All input scores must be between 1 and 10.');
        }
    }

    // Define weights for each factor (sum to 1.0)
    const weights = {
        politicalStabilityScore: 0.20,
        regulatoryRiskScore: 0.15,
        tradePolicyVolatilityScore: 0.05,
        regionalConflictIntensity: 0.20,
        sanctionsRiskScore: 0.10,
        governanceEffectivenessScore: 0.10,
        socioEconomicUnrestPotential: 0.10,
        ruleOfLawScore: 0.10
    };

    // Invert scores: 1 (very high risk) becomes 10, 10 (very low risk) becomes 1.
    // This ensures higher inverted scores contribute more to the final risk score.
    const invertedScores = {
        politicalStability: 11 - politicalStabilityScore,
        regulatoryRisk: 11 - regulatoryRiskScore,
        tradePolicyVolatility: 11 - tradePolicyVolatilityScore,
        regionalConflictIntensity: 11 - regionalConflictIntensity,
        sanctionsRisk: 11 - sanctionsRiskScore,
        governanceEffectiveness: 11 - governanceEffectivenessScore,
        socioEconomicUnrestPotential: 11 - socioEconomicUnrestPotential,
        ruleOfLaw: 11 - ruleOfLawScore
    };

    // Calculate the weighted sum of inverted scores
    let weightedSum = 
        (invertedScores.politicalStability * weights.politicalStabilityScore) +
        (invertedScores.regulatoryRisk * weights.regulatoryRiskScore) +
        (invertedScores.tradePolicyVolatility * weights.tradePolicyVolatilityScore) +
        (invertedScores.regionalConflictIntensity * weights.regionalConflictIntensity) +
        (invertedScores.sanctionsRisk * weights.sanctionsRiskScore) +
        (invertedScores.governanceEffectiveness * weights.governanceEffectivenessScore) +
        (invertedScores.socioEconomicUnrestPotential * weights.socioEconomicUnrestPotential) +
        (invertedScores.ruleOfLaw * weights.ruleOfLawScore);

    // The minimum possible weighted sum (all scores are 10, inverted to 1):
    // 1 * (sum of weights) = 1
    const minWeightedSum = 1;

    // The maximum possible weighted sum (all scores are 1, inverted to 10):
    // 10 * (sum of weights) = 10
    const maxWeightedSum = 10;

    // Scale the weighted sum to a 0-100 range, where 100 is max risk
    let overallRiskScore = ((weightedSum - minWeightedSum) / (maxWeightedSum - minWeightedSum)) * 100;

    // Ensure the score is capped within 0-100 range due to potential floating point inaccuracies
    overallRiskScore = Math.max(0, Math.min(100, overallRiskScore));

    let riskLevelCategory;
    if (overallRiskScore <= 25) {
        riskLevelCategory = 'Low Risk';
    } else if (overallRiskScore <= 50) {
        riskLevelCategory = 'Moderate Risk';
    } else if (overallRiskScore <= 75) {
        riskLevelCategory = 'High Risk';
    } else {
        riskLevelCategory = 'Critical Risk';
    }

    return {
        overallRiskScore: overallRiskScore,
        riskLevelCategory: riskLevelCategory
    };
}
