function calculateRisk(inputs) {
  const { politicalStability, regulatoryEnvironment, policyShiftRisk, economicSanctionsRisk, bilateralRelations, socialUnrestIndex, corruptionPerception, investmentProtection } = inputs;

  // Input validation and default to 1 if NaN or out of bounds for calculation safety
  const validate = (val) => {
    const num = parseFloat(val);
    return isNaN(num) ? 1 : Math.max(1, Math.min(10, num));
  };

  const ps = validate(politicalStability);
  const re = validate(regulatoryEnvironment);
  const psr = validate(policyShiftRisk);
  const esr = validate(economicSanctionsRisk);
  const br = validate(bilateralRelations);
  const sui = validate(socialUnrestIndex);
  const cp = validate(corruptionPerception);
  const ip = validate(investmentProtection);

  // Define weights for each factor (sum should be 1)
  const weights = {
    politicalStability: 0.20,
    regulatoryEnvironment: 0.10,
    policyShiftRisk: 0.15,
    economicSanctionsRisk: 0.15,
    bilateralRelations: 0.10,
    socialUnrestIndex: 0.10,
    corruptionPerception: 0.10,
    investmentProtection: 0.10
  };

  // Calculate weighted sum of scores (each score is 1-10)
  let weightedSum = 
    (ps * weights.politicalStability) +
    (re * weights.regulatoryEnvironment) +
    (psr * weights.policyShiftRisk) +
    (esr * weights.economicSanctionsRisk) +
    (br * weights.bilateralRelations) +
    (sui * weights.socialUnrestIndex) +
    (cp * weights.corruptionPerception) +
    (ip * weights.investmentProtection);

  // Max possible weighted sum = 10 (if all inputs are 10)
  // Min possible weighted sum = 1 (if all inputs are 1)

  // Normalize score to 0-100% scale
  // A score of 1 becomes 0%, a score of 10 becomes 100%
  const overallRiskScore = ((weightedSum - 1) / 9) * 100;

  let riskLevelCategory;
  if (overallRiskScore < 20) {
    riskLevelCategory = 'Low Risk';
  } else if (overallRiskScore < 45) {
    riskLevelCategory = 'Moderate Risk';
  } else if (overallRiskScore < 75) {
    riskLevelCategory = 'High Risk';
  } else {
    riskLevelCategory = 'Extreme Risk';
  }

  return {
    overallRiskScore: overallRiskScore,
    riskLevelCategory: riskLevelCategory
  };
}
