function calculateImpact(inputs) {
    const {
        eventSeverity,
        geopoliticalRegion,
        economicVulnerability,
        marketLiquidity,
        commodityDependence,
        timeHorizon,
        riskAversionIndex
    } = inputs;

    // Validate and normalize inputs within expected ranges
    const severity = Math.max(1, Math.min(10, eventSeverity));
    const ecoVuln = Math.max(1, Math.min(10, economicVulnerability));
    const marketLiq = Math.max(1, Math.min(10, marketLiquidity));
    const commDepend = Math.max(1, Math.min(10, commodityDependence));
    const horizon = Math.max(1, Math.min(52, timeHorizon));
    const riskAversion = Math.max(1, Math.min(10, riskAversionIndex));

    let overallVolatilityFactor = 0; // Represents percentage increase in a volatility index
    let energySectorChange = 0; // Represents percentage change in sector value
    let defenseSectorChange = 0; // Represents percentage change in sector value (positive for growth)
    let commoditySectorChange = 0; // Represents percentage change in sector value
    let techSectorChange = 0; // Represents percentage change in sector value
    let safeHavenAssetShift = 0; // Represents percentage of capital potentially reallocated to safe havens

    // --- Step 1: Calculate Overall Market Volatility Impact (Percentage Increase in Volatility Index) ---
    // Base increase from event severity: (5% to 50% for severity 1-10)
    overallVolatilityFactor = severity * 5.0;

    // Amplify by global economic vulnerability (higher vulnerability -> higher volatility)
    overallVolatilityFactor *= (1 + (ecoVuln - 5) / 10); // Adjusts by -40% to +50% based on ecoVuln

    // Amplify by market liquidity (lower liquidity -> higher volatility)
    overallVolatilityFactor *= (1 + (10 - marketLiq) / 8); // Adjusts by +0% to +112.5% based on marketLiq

    // Amplify by market risk aversion (higher aversion -> higher volatility)
    overallVolatilityFactor *= (1 + (riskAversion - 5) / 12); // Adjusts by -33% to +41% based on riskAversion

    // Moderate impact by time horizon (longer horizons can mean prolonged or deeper impact)
    overallVolatilityFactor *= (1 + (horizon - 1) / 40); // Small increment per week, up to ~+127.5% for 52 weeks

    // Cap overall volatility increase to a realistic range (e.g., 10% to 200% increase in VIX from its current level)
    overallVolatilityFactor = Math.max(10, Math.min(200, overallVolatilityFactor));

    // --- Step 2: Calculate Safe-Haven Asset Shift (Percentage of capital reallocation) ---
    // Directly proportional to volatility impact and risk aversion, inversely proportional to liquidity
    safeHavenAssetShift = overallVolatilityFactor * 0.2; // Base on volatility factor
    safeHavenAssetShift *= (1 + (riskAversion - 5) / 10); // Amplify with risk aversion
    safeHavenAssetShift *= (1 + (10 - marketLiq) / 10); // Amplify with lower liquidity

    // Cap safe haven shift to prevent unrealistic values
    safeHavenAssetShift = Math.max(0, Math.min(30, safeHavenAssetShift)); // 0-30% potential reallocation

    // --- Step 3: Calculate Sector-Specific Impacts (Percentage Change in Sector Value) ---
    // Initial base impact magnitudes (negative for most, positive for defense)
    let baseNegativeImpact = severity * 2.5; // Scaled by severity (2.5% to 25% base drop)
    let baseDefenseBoost = severity * 1.5; // Scaled by severity (1.5% to 15% base boost)

    // Apply market conditions to base impacts for negative sectors
    baseNegativeImpact *= (1 + (ecoVuln - 5) / 10) * (1 + (10 - marketLiq) / 10) * (1 + (riskAversion - 5) / 10);
    // Apply market conditions to base impacts for defense sector
    baseDefenseBoost *= (1 + (riskAversion - 5) / 10) * (1 + (10 - marketLiq) / 20); // Liquidity can help defense as safe-ish haven

    // Assign initial impacts
    energySectorChange = -baseNegativeImpact * 1.0; // Negative impacts by default
    commoditySectorChange = -baseNegativeImpact * 0.9;
    techSectorChange = -baseNegativeImpact * 0.8;
    defenseSectorChange = baseDefenseBoost * 1.0; // Positive impact for defense

    // Adjustments based on geopolitical region (logical branching)
    const regionLower = geopoliticalRegion.toLowerCase();

    if (regionLower.includes("middle east")) {
        energySectorChange *= 2.0; // Significant negative for energy (disruption)
        defenseSectorChange *= 1.8; // Significant positive for defense
        commoditySectorChange *= 1.5;
        techSectorChange *= 0.8;
    } else if (regionLower.includes("east asia") || regionLower.includes("china") || regionLower.includes("taiwan")) {
        techSectorChange *= 2.5; // Extreme negative for tech (supply chain)
        commoditySectorChange *= 1.7;
        defenseSectorChange *= 2.0; // Extreme positive for defense
        energySectorChange *= 1.2;
    } else if (regionLower.includes("europe") || regionLower.includes("ukraine")) {
        energySectorChange *= 1.8; // High negative for energy (supply, sanctions)
        defenseSectorChange *= 2.2; // Highest positive for defense
        commoditySectorChange *= 1.6;
        techSectorChange *= 1.0;
    } else if (regionLower.includes("global")) {
        energySectorChange *= 1.3;
        defenseSectorChange *= 1.5;
        commoditySectorChange *= 1.2;
        techSectorChange *= 1.2;
    }

    // Adjustments based on global commodity dependence
    commoditySectorChange *= (1 + (commDepend - 5) / 8); // Higher dependence -> greater impact
    energySectorChange *= (1 + (commDepend - 5) / 12);

    // Apply time horizon: longer horizon can mean deeper or more sustained impacts
    energySectorChange *= (1 + (horizon - 1) / 30);
    commoditySectorChange *= (1 + (horizon - 1) / 30);
    techSectorChange *= (1 + (horizon - 1) / 30);
    defenseSectorChange *= (1 + (horizon - 1) / 40); // Defense boost might also extend/deepen

    // Final capping for sector changes to ensure realistic boundaries
    energySectorChange = Math.max(-40, Math.min(-5, energySectorChange)); // Max 40% drop, min 5% drop
    commoditySectorChange = Math.max(-35, Math.min(-5, commoditySectorChange)); // Max 35% drop, min 5% drop
    techSectorChange = Math.max(-45, Math.min(-5, techSectorChange)); // Max 45% drop, min 5% drop
    defenseSectorChange = Math.max(2, Math.min(30, defenseSectorChange)); // Min 2% gain, max 30% gain

    // Round all final values to 2 decimal places for presentation
    const round = (value) => Math.round(value * 100) / 100;

    return {
        overallVolatilityImpact: round(overallVolatilityFactor),
        energySectorImpact: round(energySectorChange),
        defenseSectorImpact: round(defenseSectorChange),
        commoditySectorImpact: round(commoditySectorChange),
        techSectorImpact: round(techSectorChange),
        safeHavenAssetShift: round(safeHavenAssetShift)
    };
}
