function calculate({ expectedProduction, oilPrice, capex, opexPerBarrel, projectDuration, politicalStabilityIndex, sanctionsRiskIndex, corruptionPerceptionIndex, regulatoryEnvironmentScore }) {
    // Input Validation and Clamping
    expectedProduction = Math.max(0, expectedProduction);
    oilPrice = Math.max(0, oilPrice);
    capex = Math.max(0, capex);
    opexPerBarrel = Math.max(0, opexPerBarrel);
    projectDuration = Math.max(0.1, projectDuration); // Minimum duration to avoid division by zero or nonsensical values

    politicalStabilityIndex = Math.min(10, Math.max(0, politicalStabilityIndex));
    sanctionsRiskIndex = Math.min(1, Math.max(0, sanctionsRiskIndex));
    corruptionPerceptionIndex = Math.min(1, Math.max(0, corruptionPerceptionIndex));
    regulatoryEnvironmentScore = Math.min(10, Math.max(0, regulatoryEnvironmentScore));

    // --- Step 1: Calculate Base Project Profitability (Simplified Pre-Tax Cash Flow) ---
    const totalRevenue = expectedProduction * 365 * projectDuration * oilPrice;
    const totalOpex = expectedProduction * 365 * projectDuration * opexPerBarrel;
    const netOperatingCashFlow = totalRevenue - totalOpex;
    const baseProjectValue = netOperatingCashFlow - capex; // Simple calculation, not a true NPV without discounting

    // If the project is already unprofitable before considering geopolitical risk,
    // its risk-adjusted value cannot improve.
    if (baseProjectValue <= 0) {
        return {
            overallRiskScore: 100, // Maximum risk if project is inherently unviable
            probabilityOfSevereDisruption: 100, // Very high
            riskAdjustedProjectValue: 0 // Cannot be negative for a risk-adjusted 'value'
        };
    }

    // --- Step 2: Calculate Individual Risk Factors (Normalized 0-1, 1 being highest risk) ---
    // Higher political stability index means lower risk.
    const politicalRiskFactor = (10 - politicalStabilityIndex) / 10; // 0 (stable) to 1 (unstable)
    // Sanctions risk index is already 0-1.
    const sanctionsRiskFactor = sanctionsRiskIndex; // 0 (no risk) to 1 (high risk)
    // Corruption perception index is already 0-1.
    const corruptionRiskFactor = corruptionPerceptionIndex; // 0 (low perception) to 1 (high perception)
    // Higher regulatory environment score means lower risk.
    const regulatoryRiskFactor = (10 - regulatoryEnvironmentScore) / 10; // 0 (favorable) to 1 (unfavorable)

    // --- Step 3: Aggregate Geopolitical Risk Score ---
    // Define weights for each risk factor. Sum of weights must be 1.
    const W_POLITICAL = 0.35;
    const W_SANCTIONS = 0.40;
    const W_CORRUPTION = 0.15;
    const W_REGULATORY = 0.10;

    const weightedRiskSum =
        (politicalRiskFactor * W_POLITICAL) +
        (sanctionsRiskFactor * W_SANCTIONS) +
        (corruptionRiskFactor * W_CORRUPTION) +
        (regulatoryRiskFactor * W_REGULATORY);

    // Normalize to 0-100 scale. Capped to ensure 0-100 range.
    const overallRiskScore = Math.min(100, Math.max(0, weightedRiskSum * 100));

    // --- Step 4: Calculate Probability of Severe Disruption ---
    // Map the overall risk score to a probability of disruption.
    // This is an empirical mapping; higher risk score implies higher probability.
    // Example: A risk score of 50 might imply 40% disruption risk. Capped at 99.9%.
    const probabilityOfSevereDisruption = Math.min(99.9, Math.max(0, overallRiskScore * 0.8)); // Adjust scaling as appropriate

    // --- Step 5: Calculate Risk-Adjusted Project Value ---
    // Apply a risk haircut or adjustment to the base project value.
    // A higher overall risk score leads to a larger reduction in value.
    // Max haircut could be 100% if risk is extremely high.
    const riskHaircutFactor = overallRiskScore / 100; // 0 (no haircut) to 1 (100% haircut)

    let riskAdjustedProjectValue = baseProjectValue * (1 - riskHaircutFactor);

    // Ensure the risk-adjusted value doesn't go below zero after adjustment,
    // as it represents a 'value' in this context. If the project is too risky, value is effectively zero.
    riskAdjustedProjectValue = Math.max(0, riskAdjustedProjectValue);


    return {
        overallRiskScore: overallRiskScore,
        probabilityOfSevereDisruption: probabilityOfSevereDisruption,
        riskAdjustedProjectValue: riskAdjustedProjectValue
    };
}
