function calculate() {
    const portfolioValue = inputs.portfolioValue;
    const highRiskRegionExposure = inputs.highRiskRegionExposure;
    const sanctionProneIndustryExposure = inputs.sanctionProneIndustryExposure;
    const tradeWarVulnerability = inputs.tradeWarVulnerability;
    const supplyChainConcentrationIndex = inputs.supplyChainConcentrationIndex;
    const currencyVolatilityExposure = inputs.currencyVolatilityExposure;
    const politicalEventFrequencyImpact = inputs.politicalEventFrequencyImpact;

    // Input validation for edge cases
    if (portfolioValue <= 0) {
        return {
            geopoliticalRiskScore: 0,
            potentialPortfolioValueAtRisk: 0,
            riskLevelDescriptor: "No Portfolio Value"
        };
    }

    // Normalize percentage inputs (already 0-100)
    // Normalize 0-5 scale inputs to a comparable 0-100 scale by multiplying by 20
    const normalizedTradeWarVulnerability = tradeWarVulnerability * 20;
    const normalizedSupplyChainConcentrationIndex = supplyChainConcentrationIndex * 20;
    const normalizedPoliticalEventFrequencyImpact = politicalEventFrequencyImpact * 20;

    // Define weights for each risk factor (sum of weights = 1.0)
    const w_regions = 0.25;
    const w_industries = 0.20;
    const w_trade = 0.15;
    const w_supply = 0.15;
    const w_currency = 0.10;
    const w_events = 0.15;

    // Calculate the weighted sum of risk factors
    let geopoliticalRiskScore = (
        (highRiskRegionExposure * w_regions) +
        (sanctionProneIndustryExposure * w_industries) +
        (normalizedTradeWarVulnerability * w_trade) +
        (normalizedSupplyChainConcentrationIndex * w_supply) +
        (currencyVolatilityExposure * w_currency) +
        (normalizedPoliticalEventFrequencyImpact * w_events)
    );

    // Ensure the score is capped between 0 and 100
    geopoliticalRiskScore = Math.max(0, Math.min(100, geopoliticalRiskScore));

    let potentialImpactPercentage;
    let riskLevelDescriptor;

    // Determine risk level and potential impact percentage based on the score
    if (geopoliticalRiskScore <= 25) {
        riskLevelDescriptor = "Low";
        // Interpolate between 0.5% and 2.0% for Low risk
        potentialImpactPercentage = 0.005 + (geopoliticalRiskScore / 25) * (0.02 - 0.005);
    } else if (geopoliticalRiskScore <= 50) {
        riskLevelDescriptor = "Moderate";
        // Interpolate between 2.0% and 5.0% for Moderate risk
        potentialImpactPercentage = 0.02 + ((geopoliticalRiskScore - 25) / 25) * (0.05 - 0.02);
    } else if (geopoliticalRiskScore <= 75) {
        riskLevelDescriptor = "High";
        // Interpolate between 5.0% and 10.0% for High risk
        potentialImpactPercentage = 0.05 + ((geopoliticalRiskScore - 50) / 25) * (0.10 - 0.05);
    } else {
        riskLevelDescriptor = "Critical";
        // Interpolate between 10.0% and 20.0% for Critical risk
        potentialImpactPercentage = 0.10 + ((geopoliticalRiskScore - 75) / 25) * (0.20 - 0.10);
    }

    const potentialPortfolioValueAtRisk = portfolioValue * potentialImpactPercentage;

    return {
        geopoliticalRiskScore: geopoliticalRiskScore,
        potentialPortfolioValueAtRisk: potentialPortfolioValueAtRisk,
        riskLevelDescriptor: riskLevelDescriptor
    };
}
