function calculate(inputs) {
  const { initialPortfolioValue, equityAllocation, fixedIncomeAllocation, alternativeAllocation, baselineAnnualReturn, baselineAnnualVolatility, geopoliticalEventType, eventProbability, eventSeverity } = inputs;

  // Input validation and normalization
  const totalAllocation = equityAllocation + fixedIncomeAllocation + alternativeAllocation;
  if (totalAllocation === 0) {
    return {
      projectedAnnualReturn: 0,
      projectedAnnualVolatility: 0,
      projectedPortfolioValue: initialPortfolioValue,
      riskAdjustedReturn: 0
    };
  }
  if (totalAllocation !== 100) {
      // Normalize allocations if they don't sum to 100
      const normFactor = 100 / totalAllocation;
      equityAllocation *= normFactor;
      fixedIncomeAllocation *= normFactor;
      alternativeAllocation *= normFactor;
  }

  const baselineReturnDecimal = baselineAnnualReturn / 100;
  const baselineVolatilityDecimal = baselineAnnualVolatility / 100;
  const probFactor = eventProbability / 100;
  const severityFactor = eventSeverity / 5; // Scale 1-5 to 0.2-1.0

  // Internal mapping of geopolitical event types to typical market impacts
  // Values represent average percentage points impact on return/volatility for a 'max' event (Prob=100, Severity=5)
  const eventImpacts = {
    majorInterstateConflict: { equityReturn: -15, fixedIncomeReturn: -3, altReturn: 8, equityVol: 10, fixedIncomeVol: 3, altVol: 2 },
    regionalInstability: { equityReturn: -7, fixedIncomeReturn: -1, altReturn: 3, equityVol: 5, fixedIncomeVol: 1, altVol: 1 },
    globalTradeDisruption: { equityReturn: -10, fixedIncomeReturn: -2, altReturn: 5, equityVol: 7, fixedIncomeVol: 2, altVol: 1.5 },
    energySupplyShock: { equityReturn: -8, fixedIncomeReturn: -1.5, altReturn: 10, equityVol: 6, fixedIncomeVol: 1.5, altVol: 3 },
    cyberattackFinancial: { equityReturn: -12, fixedIncomeReturn: -4, altReturn: 4, equityVol: 8, fixedIncomeVol: 4, altVol: 1.8 }
  };

  const selectedEvent = eventImpacts[geopoliticalEventType];
  if (!selectedEvent) {
    // Fallback if event type not found, though dropdown should prevent this
    return {
      projectedAnnualReturn: baselineAnnualReturn,
      projectedAnnualVolatility: baselineAnnualVolatility,
      projectedPortfolioValue: initialPortfolioValue * (1 + baselineReturnDecimal),
      riskAdjustedReturn: baselineReturnDecimal / baselineVolatilityDecimal // Simple Sharpe-like without risk-free rate
    };
  }

  // Calculate scaled geopolitical impact on returns for each asset class
  const scaledEquityReturnImpact = selectedEvent.equityReturn / 100 * probFactor * severityFactor;
  const scaledFixedIncomeReturnImpact = selectedEvent.fixedIncomeReturn / 100 * probFactor * severityFactor;
  const scaledAltReturnImpact = selectedEvent.altReturn / 100 * probFactor * severityFactor;

  // Calculate total portfolio return impact based on allocation
  const totalReturnImpact = 
    (equityAllocation / 100 * scaledEquityReturnImpact) +
    (fixedIncomeAllocation / 100 * scaledFixedIncomeReturnImpact) +
    (alternativeAllocation / 100 * scaledAltReturnImpact);

  const projectedReturnDecimal = baselineReturnDecimal + totalReturnImpact;

  // Calculate scaled geopolitical impact on volatility for each asset class
  // Volatility impact is generally additive and positive (uncertainty increases volatility)
  const scaledEquityVolImpact = selectedEvent.equityVol / 100 * probFactor * severityFactor;
  const scaledFixedIncomeVolImpact = selectedEvent.fixedIncomeVol / 100 * probFactor * severityFactor;
  const scaledAltVolImpact = selectedEvent.altVol / 100 * probFactor * severityFactor;

  // For simplicity, we'll assume volatility impacts are weighted and additive to baseline
  const totalVolatilityImpact = 
    (equityAllocation / 100 * scaledEquityVolImpact) +
    (fixedIncomeAllocation / 100 * scaledFixedIncomeVolImpact) +
    (alternativeAllocation / 100 * scaledAltVolImpact);

  let projectedVolatilityDecimal = baselineVolatilityDecimal + totalVolatilityImpact;

  // Cap projected volatility at a reasonable maximum (e.g., 50% for extreme scenarios)
  projectedVolatilityDecimal = Math.min(projectedVolatilityDecimal, 0.50);
  // Ensure projected volatility is not negative
  projectedVolatilityDecimal = Math.max(projectedVolatilityDecimal, 0.01);

  // Calculate projected portfolio value after one year
  const projectedPortfolioValue = initialPortfolioValue * (1 + projectedReturnDecimal);

  // Calculate risk-adjusted return (Sharpe-like ratio, ignoring risk-free rate for simplicity)
  const riskAdjustedReturn = (projectedVolatilityDecimal === 0) ? (projectedReturnDecimal > 0 ? Infinity : -Infinity) : projectedReturnDecimal / projectedVolatilityDecimal;

  return {
    projectedAnnualReturn: projectedReturnDecimal * 100,
    projectedAnnualVolatility: projectedVolatilityDecimal * 100,
    projectedPortfolioValue: projectedPortfolioValue,
    riskAdjustedReturn: riskAdjustedReturn
  };
}
