function calculate({ initialPortfolioValue, baselineAnnualReturn, baselineAnnualVolatility, riskFreeRate, timeHorizonYears, geopoliticalEventSeverity, portfolioExposureToEvent, marketSensitivityFactor }) {
    // Input validation and normalization
    initialPortfolioValue = Math.max(1, initialPortfolioValue);
    baselineAnnualReturn = baselineAnnualReturn / 100; // to decimal
    baselineAnnualVolatility = baselineAnnualVolatility / 100; // to decimal
    riskFreeRate = riskFreeRate / 100; // to decimal
    timeHorizonYears = Math.max(1, Math.min(30, timeHorizonYears)); // Cap time horizon to 1-30 years for realistic projection
    geopoliticalEventSeverity = Math.max(1, Math.min(10, geopoliticalEventSeverity));
    portfolioExposureToEvent = Math.max(0, Math.min(100, portfolioExposureToEvent)) / 100; // to decimal
    marketSensitivityFactor = Math.max(1, Math.min(5, marketSensitivityFactor));

    // Step 1: Calculate the overall stress magnitude based on event characteristics
    const severityFactor = geopoliticalEventSeverity / 10; // Normalized 0.1 to 1.0
    const marketImpactFactor = marketSensitivityFactor / 5; // Normalized 0.2 to 1.0
    const overallStressMagnitude = severityFactor * portfolioExposureToEvent * marketImpactFactor; // Combined impact factor, 0 to 1.0

    // Step 2: Determine the direct capital loss percentage due to the event's immediate shock
    // This models a sudden, upfront impact on the portfolio's principal.
    // The loss scales with the overall stress magnitude, baseline volatility, and a base vulnerability factor.
    const baseVulnerability = 0.4; // A constant representing a base percentage of capital susceptible to immediate loss
    let directCapitalLossPercentage = overallStressMagnitude * baseVulnerability * (1 + baselineAnnualVolatility * 0.5); // Volatility amplifies direct loss
    // Cap direct loss at a realistic maximum to prevent illogical outcomes (e.g., complete wipeout without extreme inputs)
    directCapitalLossPercentage = Math.min(0.75, Math.max(0, directCapitalLossPercentage)); // Cap loss between 0% and 75%

    // Step 3: Calculate the stressed annual return percentage for the investment horizon
    // Geopolitical events typically erode future return potential.
    const returnReductionFactor = overallStressMagnitude * 0.8; // How much stress reduces expected future return
    let stressedAnnualReturn = baselineAnnualReturn * (1 - returnReductionFactor);
    // Floor for stressed return to prevent unrealistic negative projections for moderate events
    stressedAnnualReturn = Math.max(-0.20, stressedAnnualReturn); // Stressed return cannot go below -20% annually

    // Step 4: Calculate the stressed annual volatility percentage
    // Geopolitical uncertainty inherently increases market volatility and risk.
    const volatilityIncreaseFactor = overallStressMagnitude * 1.5; // How much stress amplifies market volatility
    let stressedAnnualVolatility = baselineAnnualVolatility * (1 + volatilityIncreaseFactor);
    // Ensure stressed annual volatility stays within realistic, but higher, bounds
    stressedAnnualVolatility = Math.min(0.60, Math.max(0.05, stressedAnnualVolatility)); // Cap volatility between 5% and 60%

    // Step 5: Project the portfolio value under stress
    // Apply the initial direct capital loss, then project future growth based on the stressed annual return over the time horizon.
    const valueAfterInitialLoss = initialPortfolioValue * (1 - directCapitalLossPercentage);
    const stressAdjustedPortfolioValue = valueAfterInitialLoss * Math.pow(1 + stressedAnnualReturn, timeHorizonYears);

    // Step 6: Calculate potential total capital loss from the initial portfolio value
    // This is the difference between the initial value and the final projected stressed value.
    const potentialCapitalLoss = initialPortfolioValue - stressAdjustedPortfolioValue;

    // Step 7: Calculate the stressed Sharpe Ratio, a key risk-adjusted performance metric
    // Measures the excess return per unit of total risk (volatility) under stressed conditions.
    let stressedSharpeRatio = 0;
    if (stressedAnnualVolatility > 0) {
        stressedSharpeRatio = (stressedAnnualReturn - riskFreeRate) / stressedAnnualVolatility;
    } else {
        stressedSharpeRatio = -Infinity; // Indicate extremely high risk for zero volatility
    }

    // Step 8: Calculate the Geopolitical Stress Impact Score (0-100)
    // A composite score reflecting the overall severity of the event's impact.
    // Weighted sum of direct loss, return reduction, and volatility increase.
    const scoreFromLoss = directCapitalLossPercentage * 50; // Direct loss contributes up to 50 points (max 75% loss * 50)
    const scoreFromReturnReduction = (baselineAnnualReturn > 0 ? (baselineAnnualReturn - stressedAnnualReturn) / baselineAnnualReturn : 0) * 25; // Contribution from return erosion
    const scoreFromVolatilityIncrease = (stressedAnnualVolatility - baselineAnnualVolatility) / baselineAnnualVolatility * 25; // Contribution from increased risk

    let stressImpactScore = scoreFromLoss + Math.max(0, scoreFromReturnReduction) + Math.max(0, scoreFromVolatilityIncrease);
    // Cap the impact score between 0 and 100 for easy interpretation.
    stressImpactScore = Math.min(100, Math.max(0, stressImpactScore));

    return {
        stressAdjustedPortfolioValue: Math.round(stressAdjustedPortfolioValue * 100) / 100,
        potentialCapitalLoss: Math.round(potentialCapitalLoss * 100) / 100,
        stressedAnnualReturnPercentage: Math.round(stressedAnnualReturn * 10000) / 100,
        stressedAnnualVolatilityPercentage: Math.round(stressedAnnualVolatility * 10000) / 100,
        stressImpactScore: Math.round(stressImpactScore),
        stressedSharpeRatio: Math.round(stressedSharpeRatio * 100) / 100
    };
}
