function calculate() {
    const initialPortfolioValue = parseFloat(inputs.initialPortfolioValue);
    const currentEquityAllocation = parseFloat(inputs.currentEquityAllocation);
    const currentFixedIncomeAllocation = parseFloat(inputs.currentFixedIncomeAllocation);
    const currentAlternativesAllocation = parseFloat(inputs.currentAlternativesAllocation);
    const geopoliticalRiskScore = parseFloat(inputs.geopoliticalRiskScore);
    const riskTolerance = inputs.riskTolerance;
    const targetAnnualReturn = parseFloat(inputs.targetAnnualReturn);
    const expectedVolatilityImpact = parseFloat(inputs.expectedVolatilityImpact); // as %
    const marketCorrelationToGeopolitics = parseFloat(inputs.marketCorrelationToGeopolitics); // as %

    // Input Validation
    const totalCurrentAllocation = currentEquityAllocation + currentFixedIncomeAllocation + currentAlternativesAllocation;
    if (Math.abs(totalCurrentAllocation - 100) > 0.01) { // Allow for floating point inaccuracies
        throw new Error("Current asset allocations must sum up to 100%. Please adjust.");
    }
    if (initialPortfolioValue <= 0) {
        throw new Error("Initial portfolio value must be positive.");
    }
    if (geopoliticalRiskScore < 0 || geopoliticalRiskScore > 10) {
        throw new Error("Geopolitical Risk Score must be between 0 and 10.");
    }
    if (targetAnnualReturn < 0 || targetAnnualReturn > 30) {
        throw new Error("Target Annual Return must be between 0% and 30%.");
    }
    if (expectedVolatilityImpact < 0 || expectedVolatilityImpact > 20) {
        throw new Error("Expected Volatility Impact must be between 0% and 20%.");
    }
    if (marketCorrelationToGeopolitics < 0 || marketCorrelationToGeopolitics > 100) {
        throw new Error("Market Correlation to Geopolitics must be between 0% and 100%.");
    }

    // Step 1: Calculate Geopolitical Impact Factor
    // This factor represents the overall magnitude of geopolitical risk and its market relevance.
    // Normalized risk score * (volatility impact / 100) * (market correlation / 100)
    const geopoliticalImpactFactor = (geopoliticalRiskScore / 10) * (expectedVolatilityImpact / 100) * (marketCorrelationToGeopolitics / 100);
    // Max geopoliticalImpactFactor = 1 * 0.20 * 1 = 0.20 (20%) if all inputs are max.

    // Step 2: Determine Base Allocation Weights based on Risk Tolerance
    let baseEquity, baseFixedIncome, baseAlternatives;
    let riskToleranceAdjustmentMultiplier; // Higher for more risk-averse, lower for aggressive

    switch (riskTolerance) {
        case 'Conservative':
            baseEquity = 30; baseFixedIncome = 60; baseAlternatives = 10;
            riskToleranceAdjustmentMultiplier = 1.0; // Strongest adjustment for conservative
            break;
        case 'Moderate':
            baseEquity = 50; baseFixedIncome = 40; baseAlternatives = 10;
            riskToleranceAdjustmentMultiplier = 0.6; // Moderate adjustment
            break;
        case 'Aggressive':
            baseEquity = 70; baseFixedIncome = 20; baseAlternatives = 10;
            riskToleranceAdjustmentMultiplier = 0.3; // Least adjustment for aggressive
            break;
        default:
            baseEquity = 50; baseFixedIncome = 40; baseAlternatives = 10;
            riskToleranceAdjustmentMultiplier = 0.6;
            break;
    }

    // Step 3: Blend Current and Base Allocations
    // This blend allows the current portfolio to influence the starting point, not just the base.
    const blendFactor = 0.5; // How much current allocation influences the starting point vs. base
    let blendedEquity = (currentEquityAllocation * blendFactor) + (baseEquity * (1 - blendFactor));
    let blendedFixedIncome = (currentFixedIncomeAllocation * blendFactor) + (baseFixedIncome * (1 - blendFactor));
    let blendedAlternatives = (currentAlternativesAllocation * blendFactor) + (baseAlternatives * (1 - blendFactor));

    // Normalize blended values if they don't sum to 100 (due to blend logic, they should, but for safety)
    let totalBlended = blendedEquity + blendedFixedIncome + blendedAlternatives;
    blendedEquity = (blendedEquity / totalBlended) * 100;
    blendedFixedIncome = (blendedFixedIncome / totalBlended) * 100;
    blendedAlternatives = (blendedAlternatives / totalBlended) * 100;


    // Step 4: Adjust Allocations based on Geopolitical Impact Factor and Risk Tolerance
    // Higher geopoliticalImpactFactor -> shift from Equity to Fixed Income/Alternatives.
    // Higher riskToleranceAdjustmentMultiplier -> larger shift.

    const maxPortfolioShift = 20; // Maximum percentage points total shift potential
    const equityShiftFactor = -1.0; // Equity goes down
    const fixedIncomeShiftFactor = 0.7; // Fixed Income goes up, mostly
    const alternativesShiftFactor = 0.3; // Alternatives go up, partly

    const totalShiftMagnitude = geopoliticalImpactFactor * riskToleranceAdjustmentMultiplier * maxPortfolioShift; // Total shift potential

    let adjustmentEquity = totalShiftMagnitude * equityShiftFactor;
    let adjustmentFixedIncome = totalShiftMagnitude * fixedIncomeShiftFactor;
    let adjustmentAlternatives = totalShiftMagnitude * alternativesShiftFactor;

    // Apply adjustments, ensuring they don't over/under shoot
    let optimizedEquity = blendedEquity + adjustmentEquity;
    let optimizedFixedIncome = blendedFixedIncome + adjustmentFixedIncome;
    let optimizedAlternatives = blendedAlternatives + adjustmentAlternatives;

    // Cap allocations to sensible ranges (e.g., min 0, max 100)
    optimizedEquity = Math.max(0, Math.min(100, optimizedEquity));
    optimizedFixedIncome = Math.max(0, Math.min(100, optimizedFixedIncome));
    optimizedAlternatives = Math.max(0, Math.min(100, optimizedAlternatives));

    // Normalize to ensure they sum to 100% after adjustments and caps
    let totalOptimized = optimizedEquity + optimizedFixedIncome + optimizedAlternatives;
    if (totalOptimized === 0) { // Edge case: avoid division by zero
        optimizedEquity = 33.33;
        optimizedFixedIncome = 33.33;
        optimizedAlternatives = 33.34;
    } else {
        optimizedEquity = (optimizedEquity / totalOptimized) * 100;
        optimizedFixedIncome = (optimizedFixedIncome / totalOptimized) * 100;
        optimizedAlternatives = (optimizedAlternatives / totalOptimized) * 100;
    }

    // Step 5: Calculate Portfolio Risk-Adjusted Return
    // This is an adjusted target annual return, reflecting the expected impact of geopolitical risk.
    // The impact factor reduces the user's target expected return.
    const riskReductionMultiplier = 1 - (geopoliticalImpactFactor * 0.75); // Apply a stronger penalty for the risk.
    let portfolioRiskAdjustedReturn = targetAnnualReturn * riskReductionMultiplier;

    // Ensure adjusted return is reasonable
    portfolioRiskAdjustedReturn = Math.max(0, portfolioRiskAdjustedReturn); // Cannot be negative
    // Cap at a reasonable fraction of target if risk is very high, e.g., max 80% of target if target is very high
    portfolioRiskAdjustedReturn = Math.min(targetAnnualReturn, portfolioRiskAdjustedReturn); // Should not exceed target

    return {
        optimizedEquityAllocation: optimizedEquity,
        optimizedFixedIncomeAllocation: optimizedFixedIncome,
        optimizedAlternativesAllocation: optimizedAlternatives,
        portfolioRiskAdjustedReturn: portfolioRiskAdjustedReturn,
        geopoliticalImpactFactor: geopoliticalImpactFactor * 100 // Display as a score 0-20
    };
}
