const { currentPortfolioValue, geopoliticalRiskSeverity, portfolioExposureAffected, marketVolatilityIndex, riskAversionLevel, currentCashAllocation, expectedMarketGrowth, investmentHorizon } = inputs;

// Step 1: Calculate Base Geopolitical Risk Impact Factor
// Normalize inputs and combine them to quantify the event's potential impact.
// Geopolitical Risk Severity (1-10): Scaled to 0.1-1.0
// Portfolio Exposure (0-100%): Scaled to 0-1.0
// Market Volatility (0-100%): Capped at 40% for linear impact, scaled to 0-1.0
const normalizedGeoRisk = geopoliticalRiskSeverity / 10;
const normalizedExposure = portfolioExposureAffected / 100;
const normalizedVolatility = Math.min(marketVolatilityIndex / 40, 1); 

// A weighted combination to determine the event's inherent impact severity.
let eventImpactFactor = (normalizedGeoRisk * 0.6) + (normalizedExposure * 0.3) + (normalizedVolatility * 0.1);
eventImpactFactor = Math.min(Math.max(eventImpactFactor, 0.1), 1.0); // Ensure factor is between 0.1 and 1.0

// Step 2: Incorporate Investor Risk Aversion to Determine Overall Portfolio Risk Score.
// Risk aversion acts as a multiplier, escalating the perceived impact based on the investor's comfort level with risk.
// Risk Aversion 1 (Low) to 5 (High) maps to a multiplier of 0.8 to 1.5.
const aversionMultiplier = 0.8 + ((riskAversionLevel - 1) / 4) * 0.7; 

// The overall risk score is the event impact scaled by aversion, normalized to a 1-10 scale.
const adjustedPortfolioRiskScore = Math.min(Math.max(eventImpactFactor * aversionMultiplier * 10, 1), 10); 

// Step 3: Determine Recommended Asset Allocation Targets (Cash, Defensive, Growth)
// These targets are dynamically adjusted based on the calculated overallRiskScore.
let targetCashPcnt = 0;
let targetDefensivePcnt = 0;

// Allocation logic branches based on risk score thresholds:
if (adjustedPortfolioRiskScore < 3) { // Low Risk
    targetCashPcnt = 5;
    targetDefensivePcnt = 15;
} else if (adjustedPortfolioRiskScore < 6) { // Moderate Risk
    // Linear increase for moderate risk levels
    targetCashPcnt = 10 + (adjustedPortfolioRiskScore - 3) * 3; 
    targetDefensivePcnt = 20 + (adjustedPortfolioRiskScore - 3) * 5; 
} else { // High Risk
    // Steeper linear increase for high risk levels
    targetCashPcnt = 20 + (adjustedPortfolioRiskScore - 6) * 4; 
    targetDefensivePcnt = 35 + (adjustedPortfolioRiskScore - 6) * 6; 
}

// Apply reasonable caps to ensure realistic and balanced allocations.
targetCashPcnt = Math.min(targetCashPcnt, 40); // Maximum 40% cash allocation
targetDefensivePcnt = Math.min(targetDefensivePcnt, 60); // Maximum 60% defensive assets

// Ensure combined 'safety' allocations (cash + defensive) do not exceed a practical limit, reserving space for growth assets.
let combinedSafetyPcnt = targetCashPcnt + targetDefensivePcnt;
if (combinedSafetyPcnt > 95) {
    const diff = combinedSafetyPcnt - 95;
    targetDefensivePcnt = Math.max(targetDefensivePcnt - diff, 0); // Prioritize reducing defensive if over, but not below zero
    if (targetDefensivePcnt < 0.1 && targetCashPcnt > 95) { // If defensive reduced to near zero, cap cash
        targetCashPcnt = 95;
    }
}

// The remainder of the portfolio is allocated to growth assets.
const targetGrowthPcnt = 100 - targetCashPcnt - targetDefensivePcnt;

// Step 4: Calculate Revised Expected Portfolio Return
// The revised return is influenced by the baseline market growth, the proportion of growth assets,
// and a discount factor based on the overall portfolio risk score.
const growthImpactFactor = (targetGrowthPcnt / 100); 
const riskDiscountFactor = 1 - (adjustedPortfolioRiskScore / 10) * 0.3; // Up to a 30% reduction in expected return due to high risk
let revisedExpectedReturn = expectedMarketGrowth * growthImpactFactor * riskDiscountFactor;

// Ensure the revised expected return remains within plausible bounds.
revisedExpectedReturn = Math.max(Math.min(revisedExpectedReturn, expectedMarketGrowth * 0.95), 0.5); // Min 0.5% return, max 95% of baseline growth

// Special consideration for portfolios with minimal growth asset allocation.
if (targetGrowthPcnt <= 5) {
    revisedExpectedReturn = Math.min(revisedExpectedReturn, 2.5); // Cap at 2.5% for heavily safety-oriented portfolios
}

return {
    recommendedCashTarget: parseFloat(targetCashPcnt.toFixed(2)),
    recommendedDefensiveAssetTarget: parseFloat(targetDefensivePcnt.toFixed(2)),
    recommendedGrowthAssetTarget: parseFloat(targetGrowthPcnt.toFixed(2)),
    adjustedPortfolioRiskScore: parseFloat(adjustedPortfolioRiskScore.toFixed(2)),
    revisedExpectedReturn: parseFloat(revisedExpectedReturn.toFixed(2))
};
