const expectedReturnDecimal = inputs.expectedReturn / 100;
const stdDevDecimal = inputs.stdDev / 100;
const riskFreeRateDecimal = inputs.riskFreeRate / 100;
const countryExposureDecimal = inputs.countryExposure / 100;

// Calculate Geopolitical Risk Score (GRS) for the region/country
// GRS ranges from 0 (lowest risk: risk index 1, stability 1) to 0.9 (highest risk: risk index 9, stability 0.1)
// Let's normalize risk index to 0-1 range first, (riskIndex-1)/9 for 0 to 1, then multiply by (1-stability)
const normalizedRiskIndex = (inputs.geopoliticalRiskIndex - 1) / 9; // 0 to 1
const geopoliticalRiskScore = normalizedRiskIndex * (1 - inputs.politicalStabilityFactor);

// Calculate the Total Geopolitical Risk Deduction (TGRD) as an absolute percentage deduction
// This deduction scales with the country exposure and the GRS.
// The scaling factor (e.g., * 3) can be adjusted to control the impact magnitude.
let riskDeductionAbsolute = countryExposureDecimal * geopoliticalRiskScore * (expectedReturnDecimal * 3); 

// Cap the deduction to prevent excessive negative adjusted returns 
// (e.g., ensure adjusted return is not less than 25% of the original expected return, or 0 if expected is already low)
riskDeductionAbsolute = Math.min(riskDeductionAbsolute, expectedReturnDecimal * 0.75); // Max 75% of expected return can be deducted
riskDeductionAbsolute = Math.max(riskDeductionAbsolute, 0); // Ensure deduction is not negative

// Calculate the Geopolitical Risk-Adjusted Return (in decimal)
const adjustedReturnDecimal = expectedReturnDecimal - riskDeductionAbsolute;

// Calculate the Geopolitical Risk-Adjusted Standard Deviation
// Geopolitical risk not only reduces return but also tends to increase perceived volatility.
// We scale up the standard deviation based on the combined impact of exposure and risk score.
const adjustedStdDevDecimal = stdDevDecimal * (1 + (countryExposureDecimal * geopoliticalRiskScore));

// Calculate the Geopolitical Risk-Adjusted Sharpe Ratio
// Ensure adjustedStdDevDecimal is not zero to prevent division errors. If it is, Sharpe is 0.
const adjustedSharpe = adjustedStdDevDecimal > 0 ? (adjustedReturnDecimal - riskFreeRateDecimal) / adjustedStdDevDecimal : 0;

return {
    adjustedReturn: adjustedReturnDecimal * 100, // Convert back to percentage
    riskDeduction: riskDeductionAbsolute * 100, // Convert back to percentage
    adjustedSharpe: adjustedSharpe
};
