function calculate({ currentPortfolioValue, currentEquityAllocationPct, currentBondAllocationPct, currentCommodityAllocationPct, eventProbability, globalEquityImpactPct, globalBondImpactPct, commodityImpactPct, riskAversionLevel }) {

    let geopoliticalRiskScore = 0;
    let recommendedEquityAllocationChange = 0;
    let recommendedBondAllocationChange = 0;
    let recommendedCommodityAllocationChange = 0;
    let adjustedPortfolioValue = currentPortfolioValue;

    // Input validation for allocations
    const totalCurrentAllocation = currentEquityAllocationPct + currentBondAllocationPct + currentCommodityAllocationPct;
    if (totalCurrentAllocation === 0) {
        return {
            geopoliticalRiskScore: 0,
            recommendedEquityAllocationChange: 0,
            recommendedBondAllocationChange: 0,
            recommendedCommodityAllocationChange: 0,
            adjustedPortfolioValue: currentPortfolioValue,
            error: "Total current allocation cannot be zero."
        };
    }
    // Normalize allocations if not exactly 100, assuming they are proportions
    const normEquityAlloc = (currentEquityAllocationPct / totalCurrentAllocation) * 100;
    const normBondAlloc = (currentBondAllocationPct / totalCurrentAllocation) * 100;
    const normCommodityAlloc = (currentCommodityAllocationPct / totalCurrentAllocation) * 100;

    // Step 1: Calculate Weighted Portfolio Impact & Geopolitical Risk Score
    const totalWeightedImpactRaw = 
        (normEquityAlloc * globalEquityImpactPct) +
        (normBondAlloc * globalBondImpactPct) +
        (normCommodityAlloc * commodityImpactPct);

    // Geopolitical Risk Score calculation (scaled to 0-100)
    // Factor of 5 chosen so that max impact (e.g., 20%) * prob (100%) * risk (5/5) results in 100 score
    if (eventProbability > 0 && Math.abs(totalWeightedImpactRaw) > 0) {
        geopoliticalRiskScore = Math.min(100, Math.max(0, 
            (eventProbability / 100) * 
            (Math.abs(totalWeightedImpactRaw / 100)) * 
            (riskAversionLevel / 5) * 500
        ));
    } else {
        geopoliticalRiskScore = 0;
    }

    // If risk score is 0, no adjustments are needed
    if (geopoliticalRiskScore === 0) {
        const unadjustedImpactDecimal = totalWeightedImpactRaw / 10000; // raw impact / 100 (for %) / 100 (for %) = decimal
        adjustedPortfolioValue = currentPortfolioValue * (1 + (unadjustedImpactDecimal * (eventProbability / 100)));
        return {
            geopoliticalRiskScore: parseFloat(geopoliticalRiskScore.toFixed(0)),
            recommendedEquityAllocationChange: 0,
            recommendedBondAllocationChange: 0,
            recommendedCommodityAllocationChange: 0,
            adjustedPortfolioValue: parseFloat(adjustedPortfolioValue.toFixed(2))
        };
    }

    // Step 2: Determine Shift Magnitude
    // This factor dictates how aggressively to adjust the portfolio (0 to max_cap)
    let baseShiftFactor = (geopoliticalRiskScore / 100) * (riskAversionLevel / 5);
    baseShiftFactor = Math.min(0.35, baseShiftFactor); // Cap max relative shift to 35% of current allocation for realism

    // Step 3: Calculate Decreases from Negatively Impacted Assets
    let tempEquityChange = 0;
    let tempBondChange = 0;
    let tempCommodityChange = 0;
    let totalNegativeShiftTarget = 0; // The total percentage points to reallocate

    if (globalEquityImpactPct < 0) {
        const decreaseAmount = normEquityAlloc * (Math.abs(globalEquityImpactPct) / 100) * baseShiftFactor; // Pct points
        tempEquityChange = -Math.min(normEquityAlloc, decreaseAmount); // Ensure we don't go below 0 allocation
        totalNegativeShiftTarget += Math.abs(tempEquityChange);
    }

    if (globalBondImpactPct < 0) {
        const decreaseAmount = normBondAlloc * (Math.abs(globalBondImpactPct) / 100) * baseShiftFactor; // Pct points
        tempBondChange = -Math.min(normBondAlloc, decreaseAmount); 
        totalNegativeShiftTarget += Math.abs(tempBondChange);
    }

    if (commodityImpactPct < 0) {
        const decreaseAmount = normCommodityAlloc * (Math.abs(commodityImpactPct) / 100) * baseShiftFactor; // Pct points
        tempCommodityChange = -Math.min(normCommodityAlloc, decreaseAmount); 
        totalNegativeShiftTarget += Math.abs(tempCommodityChange);
    }

    // Step 4: Distribute Total Negative Shift to Positively/Least-Negatively Impacted Assets
    const gainAssetsInfo = [];
    if (globalEquityImpactPct >= 0) gainAssetsInfo.push({ type: 'equity', currentAlloc: normEquityAlloc, impact: globalEquityImpactPct });
    if (globalBondImpactPct >= 0) gainAssetsInfo.push({ type: 'bond', currentAlloc: normBondAlloc, impact: globalBondImpactPct });
    if (commodityImpactPct >= 0) gainAssetsInfo.push({ type: 'commodity', currentAlloc: normCommodityAlloc, impact: commodityImpactPct });

    let totalGainWeight = gainAssetsInfo.reduce((sum, asset) => sum + (asset.currentAlloc * Math.max(0.1, asset.impact / 10)), 0); // Use small factor for 0 impact, larger for positive
    if (totalGainWeight === 0 && gainAssetsInfo.length > 0) {
        // If all positive assets have 0 impact, give them equal weight for distribution
        totalGainWeight = gainAssetsInfo.reduce((sum, asset) => sum + asset.currentAlloc, 0);
        gainAssetsInfo.forEach(asset => asset.impact = 1); // Normalize for distribution
    }

    if (totalGainWeight > 0 && totalNegativeShiftTarget > 0) {
        for (const asset of gainAssetsInfo) {
            const increaseAmount = totalNegativeShiftTarget * ((asset.currentAlloc * (asset.impact / 10)) / totalGainWeight); // Pct points
            if (asset.type === 'equity') tempEquityChange += increaseAmount;
            else if (asset.type === 'bond') tempBondChange += increaseAmount;
            else if (asset.type === 'commodity') tempCommodityChange += increaseAmount;
        }
    } else if (totalNegativeShiftTarget > 0) {
        // If no assets benefit (all are negative or neutral), the shift is effectively to cash/reduced exposure.
        // The changes remain as negative, reflecting a net reduction from these asset classes.
        // In this case, the sum of changes won't be zero, implying a move to highly liquid assets (cash).
    }

    // Cap changes to prevent allocation going below zero or exceeding 100% (after applying adjustments)
    recommendedEquityAllocationChange = Math.max(-normEquityAlloc, tempEquityChange);
    recommendedBondAllocationChange = Math.max(-normBondAlloc, tempBondChange);
    recommendedCommodityAllocationChange = Math.max(-normCommodityAlloc, tempCommodityChange);

    // Step 5: Calculate Adjusted Portfolio Value (projected post-adjustment)
    const finalEquityAlloc = normEquityAlloc + recommendedEquityAllocationChange;
    const finalBondAlloc = normBondAlloc + recommendedBondAllocationChange;
    const finalCommodityAlloc = normCommodityAllocationPct + recommendedCommodityAllocationChange;

    // Calculate projected impact on the portfolio *after* the recommended adjustments have been made
    const projectedImpactOnAdjustedPortfolioDecimal = 
        (finalEquityAlloc / 100 * globalEquityImpactPct) +
        (finalBondAlloc / 100 * globalBondImpactPct) +
        (finalCommodityAlloc / 100 * commodityImpactPct);

    adjustedPortfolioValue = currentPortfolioValue * (1 + (projectedImpactOnAdjustedPortfolioDecimal / 100 * (eventProbability / 100)));

    // Ensure output values are parsed to float with desired precision
    return {
        geopoliticalRiskScore: parseFloat(geopoliticalRiskScore.toFixed(0)),
        recommendedEquityAllocationChange: parseFloat(recommendedEquityAllocationChange.toFixed(2)),
        recommendedBondAllocationChange: parseFloat(recommendedBondAllocationChange.toFixed(2)),
        recommendedCommodityAllocationChange: parseFloat(recommendedCommodityAllocationChange.toFixed(2)),
        adjustedPortfolioValue: parseFloat(adjustedPortfolioValue.toFixed(2))
    };
}
