function calculate({ initialExpectedReturn, initialVolatility, equityAllocation, sanctionsImpactFactor, tradeConflictImpactFactor, politicalInstabilityImpactFactor }) {
  // Input validation and normalization
  initialExpectedReturn = Math.max(-10, parseFloat(initialExpectedReturn) || 0);
  initialVolatility = Math.max(0, parseFloat(initialVolatility) || 0);
  equityAllocation = Math.min(100, Math.max(0, parseFloat(equityAllocation) || 0));
  sanctionsImpactFactor = Math.min(10, Math.max(0, parseFloat(sanctionsImpactFactor) || 0));
  tradeConflictImpactFactor = Math.min(10, Math.max(0, parseFloat(tradeConflictImpactFactor) || 0));
  politicalInstabilityImpactFactor = Math.min(10, Math.max(0, parseFloat(politicalInstabilityImpactFactor) || 0));

  const equityRatio = equityAllocation / 100;

  // Calculate a composite geopolitical risk score (normalized 0-1)
  const totalImpactScore = sanctionsImpactFactor + tradeConflictImpactFactor + politicalInstabilityImpactFactor;
  const compositeRiskScore = totalImpactScore / 30; // Max score is 30, so normalize to 1

  // --- Calculate Impact on Expected Return (Risk Premium) ---
  // Base reduction factor: higher risk score means higher reduction
  // Max base reduction can be around 3% for max composite risk
  const baseReturnReductionFactor = compositeRiskScore * 0.035; 

  // Equity allocation amplifies the return reduction. More equity = more exposure.
  // Equity amplifier ranges from 1 (0% equity) to 1.6 (100% equity).
  const equityReturnAmplifier = 1 + (equityRatio * 0.6);

  // Total risk premium reduction as a decimal
  let riskPremiumReductionDecimal = baseReturnReductionFactor * equityReturnAmplifier;

  // Cap the maximum reduction to ensure realism (e.g., max 7% absolute reduction)
  riskPremiumReductionDecimal = Math.min(riskPremiumReductionDecimal, initialExpectedReturn / 100 + 0.05); // Cannot reduce more than current return + 5%
  riskPremiumReductionDecimal = Math.min(riskPremiumReductionDecimal, 0.07); // Absolute max reduction 7%

  // Convert to percentage for display
  const riskPremiumAdded = riskPremiumReductionDecimal * 100;

  // Calculate adjusted expected return
  const adjustedExpectedReturn = Math.max(-10, initialExpectedReturn - riskPremiumAdded); // Floor at -10%

  // --- Calculate Impact on Volatility ---
  // Base volatility increase factor: higher risk score means higher volatility
  // Max base increase can be around 25% for max composite risk
  const baseVolatilityIncreaseFactor = compositeRiskScore * 0.25; 

  // Equity allocation amplifies the volatility increase. More equity = more volatility.
  // Equity amplifier ranges from 1 (0% equity) to 1.5 (100% equity).
  const equityVolatilityAmplifier = 1 + (equityRatio * 0.5);

  // Total volatility increase as a decimal
  let volatilityIncreaseDecimal = baseVolatilityIncreaseFactor * equityVolatilityAmplifier;

  // Cap the maximum volatility increase to ensure realism (e.g., max 60% increase from initial)
  volatilityIncreaseDecimal = Math.min(volatilityIncreaseDecimal, 0.60); // Max 60% increase of initial volatility

  // Calculate adjusted volatility
  const adjustedVolatility = Math.min(initialVolatility * (1 + volatilityIncreaseDecimal), 50); // Cap absolute volatility at 50%

  return {
    adjustedExpectedReturn: adjustedExpectedReturn,
    adjustedVolatility: adjustedVolatility,
    riskPremiumAdded: riskPremiumAdded
  };
}
