const { originalRouteDistance, detourRouteDistance, averageVesselSpeedKnots, dailyFuelCost, dailyVesselOperatingCost, cargoValue, insurancePremiumIncrease, potentialTariffRate } = inputs;

const HOURS_PER_DAY = 24;

// Calculate original and detour voyage durations
const originalVoyageDurationHours = originalRouteDistance / averageVesselSpeedKnots;
const detourVoyageDurationHours = detourRouteDistance / averageVesselSpeedKnots;

const originalVoyageDurationDays = originalVoyageDurationHours / HOURS_PER_DAY;
const detourVoyageDurationDays = detourVoyageDurationHours / HOURS_PER_DAY;

// Ensure detour is longer than original, otherwise additional costs are 0
const additionalDays = Math.max(0, detourVoyageDurationDays - originalVoyageDurationDays);

// Additional Fuel Cost
const additionalFuelCost = additionalDays * dailyFuelCost;

// Additional Operating Cost (excluding fuel, as fuel is separate)
const additionalOperatingCost = additionalDays * dailyVesselOperatingCost;

// Increased Insurance Cost
const increasedInsuranceCost = cargoValue * (insurancePremiumIncrease / 100);

// Potential Tariff Cost
const potentialTariffCost = cargoValue * (potentialTariffRate / 100);

// Total Additional Cost
const totalAdditionalCost = additionalFuelCost + additionalOperatingCost + increasedInsuranceCost + potentialTariffCost;

// Total Financial Impact as % of Cargo Value
const totalRiskImpactPercentage = cargoValue > 0 ? (totalAdditionalCost / cargoValue) * 100 : 0;

return {
  additionalFuelCost: additionalFuelCost,
  additionalOperatingCost: additionalOperatingCost,
  increasedInsuranceCost: increasedInsuranceCost,
  potentialTariffCost: potentialTariffCost,
  totalAdditionalCost: totalAdditionalCost,
  totalRiskImpactPercentage: totalRiskImpactPercentage
};
