const annual_oil_consumption = inputs.annual_oil_consumption;
const current_oil_price = inputs.current_oil_price;
const choke_point_exposure = inputs.choke_point_exposure;
const geopolitical_risk_index = inputs.geopolitical_risk_index;
const tanker_incident_impact_factor = inputs.tanker_incident_impact_factor;
const mitigation_readiness = inputs.mitigation_readiness;

// Constants for weighting and caps
const CHOKE_POINT_WEIGHT = 0.35;
const GEOPOLITICAL_WEIGHT = 0.45;
const INCIDENT_WEIGHT = 0.20;
const MITIGATION_MAX_REDUCTION = 0.7; // Up to 70% reduction in impact for max readiness
const MAX_PRICE_PREMIUM_PERCENT = 0.5; // Maximum 50% price premium for highest risk
const BASE_RISK_FLOOR = 5; // Minimum possible risk score
const BASE_RISK_CEILING = 95; // Maximum possible risk score
const HIGH_RISK_OPERATIONAL_OVERHEAD_PERCENT = 0.005; // 0.5% of total annual value for high risk
const HIGH_RISK_THRESHOLD = 70; // Risk score above which operational overhead applies

// Normalize inputs for calculation
const choke_norm = choke_point_exposure / 100;
const geo_norm = geopolitical_risk_index / 10; // Scales 1-10 to 0.1-1.0
const incident_norm = tanker_incident_impact_factor / 5; // Scales 0-5 to 0-1.0
const mitigation_norm = (mitigation_readiness - 1) / 4; // Scales 1-5 to 0-1.0 (0 for 1, 1 for 5)

// Calculate base weighted risk component
let base_weighted_risk = 
    (choke_norm * CHOKE_POINT_WEIGHT) +
    (geo_norm * GEOPOLITICAL_WEIGHT) +
    (incident_norm * INCIDENT_WEIGHT);

// Apply mitigation factor to reduce the base risk
const mitigation_factor = 1 - (mitigation_norm * MITIGATION_MAX_REDUCTION);
let final_risk_score_unscaled = base_weighted_risk * mitigation_factor;

// Scale the risk score to 0-100 and apply floor/ceiling
let supply_disruption_risk_score = final_risk_score_unscaled * 100;
supply_disruption_risk_score = Math.min(BASE_RISK_CEILING, Math.max(BASE_RISK_FLOOR, supply_disruption_risk_score));

// Calculate potential cost increase
const exposed_annual_barrels = annual_oil_consumption * choke_norm;

// Determine the potential price premium based on the risk score
const price_premium_percentage = MAX_PRICE_PREMIUM_PERCENT * (supply_disruption_risk_score / 100);

// Calculate direct cost impact from price premium on exposed barrels
let potential_cost_increase = exposed_annual_barrels * current_oil_price * price_premium_percentage;

// Add an additional operational overhead for very high risk scenarios
if (supply_disruption_risk_score > HIGH_RISK_THRESHOLD) {
    potential_cost_increase += annual_oil_consumption * current_oil_price * HIGH_RISK_OPERATIONAL_OVERHEAD_PERCENT;
}

return {
    potential_cost_increase: potential_cost_increase,
    supply_disruption_risk_score: supply_disruption_risk_score
};
