let baseRisk = (inputs.geopoliticalTensionIndex * 0.3) + ((11 - inputs.majorProducerStabilityRating) * 0.2) + (inputs.criticalChokepointVulnerability * 0.25);

let demandSupplyPressure = (inputs.globalDemandForecastGrowth * 0.5);
demandSupplyPressure += Math.max(0, (3 - inputs.nonOpecPlusSpareCapacity) * 0.7);

let mitigationFactor = (inputs.currentStrategicReservesDays / 100) * 1.5;
mitigationFactor += (inputs.opecPlusUnityIndex * 0.8);

let disruptionProbability = (baseRisk + demandSupplyPressure) * 5;
disruptionProbability -= mitigationFactor * 3;
disruptionProbability = Math.min(Math.max(disruptionProbability, 5), 95);

let priceSpike = disruptionProbability * 0.8;
priceSpike += Math.max(0, (120 - inputs.currentStrategicReservesDays) * 0.2);
priceSpike += Math.max(0, (5 - inputs.opecPlusUnityIndex) * 5);
priceSpike += Math.max(0, demandSupplyPressure * 2);
priceSpike = Math.min(priceSpike, 150);
priceSpike = Math.max(priceSpike, disruptionProbability < 10 ? 5 : 10);

let disruptionDuration = disruptionProbability * 0.5;
disruptionDuration += Math.max(0, (90 - inputs.currentStrategicReservesDays) * 0.3);
disruptionDuration += Math.max(0, (2 - inputs.nonOpecPlusSpareCapacity) * 10);
disruptionDuration += inputs.criticalChokepointVulnerability * 2;
disruptionDuration = Math.min(disruptionDuration, 180);
disruptionDuration = Math.max(disruptionDuration, disruptionProbability < 10 ? 3 : 7);

return {
    disruptionProbability: disruptionProbability,
    potentialPriceSpike: priceSpike,
    estimatedDisruptionDuration: disruptionDuration
};
