const cityData = {
    "new york, usa": {
        costOfLivingIndex: 100, rentIndex: 100, groceriesIndex: 100, restaurantPriceIndex: 100, purchasingPowerIndex: 100, averageIncomeTaxRate: 0.30 // Simplified for calculation
    },
    "london, uk": {
        costOfLivingIndex: 80, rentIndex: 85, groceriesIndex: 75, restaurantPriceIndex: 85, purchasingPowerIndex: 80, averageIncomeTaxRate: 0.25
    },
    "berlin, germany": {
        costOfLivingIndex: 65, rentIndex: 50, groceriesIndex: 60, restaurantPriceIndex: 65, purchasingPowerIndex: 110, averageIncomeTaxRate: 0.35
    },
    "singapore, singapore": {
        costOfLivingIndex: 85, rentIndex: 110, groceriesIndex: 80, restaurantPriceIndex: 90, purchasingPowerIndex: 95, averageIncomeTaxRate: 0.20
    },
    "toronto, canada": {
        costOfLivingIndex: 70, rentIndex: 60, groceriesIndex: 70, restaurantPriceIndex: 75, purchasingPowerIndex: 90, averageIncomeTaxRate: 0.32
    },
    "lisbon, portugal": {
        costOfLivingIndex: 45, rentIndex: 35, groceriesIndex: 40, restaurantPriceIndex: 45, purchasingPowerIndex: 70, averageIncomeTaxRate: 0.28
    },
    "san francisco, usa": {
        costOfLivingIndex: 95, rentIndex: 120, groceriesIndex: 90, restaurantPriceIndex: 105, purchasingPowerIndex: 115, averageIncomeTaxRate: 0.33
    },
    "sydney, australia": {
        costOfLivingIndex: 78, rentIndex: 90, groceriesIndex: 70, restaurantPriceIndex: 80, purchasingPowerIndex: 85, averageIncomeTaxRate: 0.29
    },
    "dubai, uae": {
        costOfLivingIndex: 70, rentIndex: 95, groceriesIndex: 65, restaurantPriceIndex: 75, purchasingPowerIndex: 100, averageIncomeTaxRate: 0.00
    },
    "tokyo, japan": {
        costOfLivingIndex: 90, rentIndex: 95, groceriesIndex: 85, restaurantPriceIndex: 90, purchasingPowerIndex: 105, averageIncomeTaxRate: 0.27
    },
    "zurich, switzerland": {
        costOfLivingIndex: 115, rentIndex: 105, groceriesIndex: 105, restaurantPriceIndex: 110, purchasingPowerIndex: 130, averageIncomeTaxRate: 0.22
    },
    "paris, france": {
        costOfLivingIndex: 88, rentIndex: 90, groceriesIndex: 80, restaurantPriceIndex: 95, purchasingPowerIndex: 90, averageIncomeTaxRate: 0.38
    }
};

const MIN_ANNUAL_GROSS_INCOME = 15000; // Minimum reasonable gross income for any destination
const MIN_ANNUAL_NET_INCOME_PER_PERSON = 10000; // Minimum reasonable net income per person for basic living

const currentCityKey = inputs.currentCity.toLowerCase();
const destinationCityKey = inputs.destinationCity.toLowerCase();

const current = cityData[currentCityKey];
const destination = cityData[destinationCityKey];

// Handle missing city data
if (!current) {
    throw new Error(`Cost of living data not found for current city: ${inputs.currentCity}. Please check spelling or choose a listed city.`);
}
if (!destination) {
    throw new Error(`Cost of living data not found for destination city: ${inputs.destinationCity}. Please check spelling or choose a listed city.`);
}

// 1. Calculate Current Net Income and Disposable Income
const currentAnnualGrossIncome = inputs.currentAnnualGrossIncome;
const currentAnnualTax = currentAnnualGrossIncome * current.averageIncomeTaxRate;
const currentAnnualNetIncome = currentAnnualGrossIncome - currentAnnualTax;

const currentAnnualHousingCost = inputs.currentMonthlyHousingCost * 12;
const currentAnnualOtherExpenses = inputs.currentMonthlyOtherExpenses * 12;

// Ensure current net income can cover current expenses to avoid negative discretionary income
const currentEssentialExpenses = currentAnnualHousingCost + currentAnnualOtherExpenses;
let currentAnnualDiscretionaryIncome = currentAnnualNetIncome - currentEssentialExpenses;

// Cap discretionary income to zero if net income can't cover essential expenses or is negative
if (currentAnnualDiscretionaryIncome < 0) {
    currentAnnualDiscretionaryIncome = 0; // Assume user will reduce discretionary spending if necessary
}

// 2. Adjust Costs for Destination
const rentRatio = destination.rentIndex / current.rentIndex;
const estimatedDestinationAnnualHousingCost = currentAnnualHousingCost * rentRatio;

// Use a weighted average for goods and services, or simple average of available indices
const currentAvgGoodsServicesIndex = (current.groceriesIndex + current.restaurantPriceIndex) / 2;
const destinationAvgGoodsServicesIndex = (destination.groceriesIndex + destination.restaurantPriceIndex) / 2;
const goodsServicesRatio = destinationAvgGoodsServicesIndex / currentAvgGoodsServicesIndex;
const estimatedDestinationAnnualGoodsServicesCost = currentAnnualOtherExpenses * goodsServicesRatio;

const purchasingPowerRatio = destination.purchasingPowerIndex / current.purchasingPowerIndex;
let estimatedDestinationAnnualDiscretionaryIncome = currentAnnualDiscretionaryIncome * purchasingPowerRatio;

// Apply desired standard of living adjustment
let standardOfLivingMultiplier = 1.0;
if (inputs.desiredStandardOfLiving === 'improve') {
    standardOfLivingMultiplier = 1.1; // 10% improvement
} else if (inputs.desiredStandardOfLiving === 'reduce') {
    standardOfLivingMultiplier = 0.9; // 10% reduction
}
estimatedDestinationAnnualDiscretionaryIncome *= standardOfLivingMultiplier;

// 3. Calculate Estimated Destination Net Income Needed
const estimatedDestinationAnnualNetIncomeNeeded = 
    estimatedDestinationAnnualHousingCost +
    estimatedDestinationAnnualGoodsServicesCost +
    estimatedDestinationAnnualDiscretionaryIncome;

// Ensure net income needed is not unrealistically low, considering household size
const minDestinationNetIncomeBasedOnHousehold = MIN_ANNUAL_NET_INCOME_PER_PERSON * inputs.householdSize;
const finalDestinationNetIncomeNeeded = Math.max(estimatedDestinationAnnualNetIncomeNeeded, minDestinationNetIncomeBasedOnHousehold);

// 4. Calculate Estimated Destination Gross Income Needed (considering new taxes)
// Gross = Net / (1 - TaxRate)
let adjustedAnnualIncomeNeeded;
if (destination.averageIncomeTaxRate >= 1) { // Handle cases where tax rate is 100% or more (e.g., error in data, or 0% tax)
    adjustedAnnualIncomeNeeded = finalDestinationNetIncomeNeeded; // If tax rate is effectively 100%, gross = net (simplified)
} else if (destination.averageIncomeTaxRate < 0) { // Handle negative tax rates (shouldn't happen, but for robustness)
     adjustedAnnualIncomeNeeded = finalDestinationNetIncomeNeeded; // No tax applied if rate is invalid
} else {
    adjustedAnnualIncomeNeeded = finalDestinationNetIncomeNeeded / (1 - destination.averageIncomeTaxRate);
}

// Ensure gross income is above a minimum threshold
adjustedAnnualIncomeNeeded = Math.max(adjustedAnnualIncomeNeeded, MIN_ANNUAL_GROSS_INCOME);

// Calculate estimated tax burden in destination
const estimatedDestinationAnnualTaxBurden = adjustedAnnualIncomeNeeded * destination.averageIncomeTaxRate;

// 5. Calculate Percentage Change
let percentageIncreaseDecrease = 0;
if (currentAnnualGrossIncome > 0) {
    percentageIncreaseDecrease = ((adjustedAnnualIncomeNeeded - currentAnnualGrossIncome) / currentAnnualGrossIncome) * 100;
} else if (adjustedAnnualIncomeNeeded > 0) {
    percentageIncreaseDecrease = Infinity; // If current income is 0 but destination income is needed
} else {
    percentageIncreaseDecrease = 0; // Both are 0 or negligible
}

return {
    adjustedAnnualIncomeNeeded: adjustedAnnualIncomeNeeded,
    percentageIncreaseDecrease: percentageIncreaseDecrease,
    estimatedDestinationAnnualHousingCost: estimatedDestinationAnnualHousingCost,
    estimatedDestinationAnnualGoodsServicesCost: estimatedDestinationAnnualGoodsServicesCost,
    estimatedDestinationAnnualTaxBurden: estimatedDestinationAnnualTaxBurden
};
