function calculate({ initialAnnualFunding, fundingChangePercentage, economicMultiplier, administrativeOverheadPercentage, serviceEfficiencyImpact, sectorVulnerabilityFactor, durationYears }) {
  // Input validation and default values
  initialAnnualFunding = Math.max(0, initialAnnualFunding || 0);
  fundingChangePercentage = fundingChangePercentage !== undefined ? fundingChangePercentage : 0;
  economicMultiplier = Math.max(0.1, economicMultiplier || 1);
  administrativeOverheadPercentage = Math.min(100, Math.max(0, administrativeOverheadPercentage || 0));
  serviceEfficiencyImpact = serviceEfficiencyImpact !== undefined ? serviceEfficiencyImpact : 0;
  sectorVulnerabilityFactor = Math.min(5, Math.max(0.1, sectorVulnerabilityFactor || 1));
  durationYears = Math.max(1, durationYears || 1);

  // Step 1: Calculate the adjusted annual funding after the proposed change
  const adjustedFundingPerYear = initialAnnualFunding * (1 + fundingChangePercentage / 100);

  // Step 2: Calculate the net funding available for direct programs/services after administrative overhead
  const netFundingAfterOverheadPerYear = adjustedFundingPerYear * (1 - administrativeOverheadPercentage / 100);

  // Edge case: If net funding is negative, cap at 0 for economic impact and service delivery calculations
  const effectiveNetFunding = Math.max(0, netFundingAfterOverheadPerYear);

  // Step 3: Calculate the total economic impact over the specified duration
  // This assumes the multiplier effect is applied to the net funding available for programs/services
  const totalEconomicImpact = effectiveNetFunding * economicMultiplier * durationYears;

  // Step 4: Calculate the annual service delivery capacity change
  // This combines the direct effect of funding change and the efficiency impact, weighted by sector vulnerability.
  // A baseline assumption: 1% funding change * vulnerability factor directly impacts services, plus efficiency changes.
  let annualServiceDeliveryImpactPercentage = (fundingChangePercentage / 100 * sectorVulnerabilityFactor * 100) + serviceEfficiencyImpact;
  // Cap this impact for realism, e.g., between -100% (complete cessation) and +100% (doubling capacity/quality)
  annualServiceDeliveryImpactPercentage = Math.max(-100, Math.min(100, annualServiceDeliveryImpactPercentage));

  // Step 5: Map the service delivery impact percentage to a 0-100 score
  // Baseline score of 70 represents 'good' current operations. A 100% positive impact leads to 100, -100% to 0.
  let averageServiceDeliveryImpactScore = 70 + (annualServiceDeliveryImpactPercentage / 2);
  averageServiceDeliveryImpactScore = Math.max(0, Math.min(100, averageServiceDeliveryImpactScore));

  return {
    netAdjustedFunding: netFundingAfterOverheadPerYear,
    totalEconomicImpact: totalEconomicImpact,
    annualServiceDeliveryImpactPercentage: annualServiceDeliveryImpactPercentage,
    averageServiceDeliveryImpactScore: averageServiceDeliveryImpactScore
  };
}
