function calculateImpact(inputs) {
  const duration = inputs.shutdownDurationDays;
  const annualGDP = inputs.totalUSAnnualGDP * 1e12; // Convert Trillions to USD
  const federalWorkers = inputs.federalWorkerHeadcount * 1e6; // Convert Millions to individuals
  const avgFederalSalary = inputs.averageFederalWorkerSalary;
  const affectedWorkersPercentage = inputs.percentageOfFederalWorkersAffected / 100;
  const dailyPrivateSectorGDPReductionBasisPoints = inputs.dailyPrivateSectorGDPReductionRate;
  const consumerConfidenceMultiplier = inputs.consumerConfidenceImpactMultiplier;

  // Input validation and edge cases
  if (duration <= 0 || annualGDP <= 0 || federalWorkers <= 0 || avgFederalSalary <= 0 || 
      affectedWorkersPercentage < 0 || affectedWorkersPercentage > 1 || 
      dailyPrivateSectorGDPReductionBasisPoints < 0 || consumerConfidenceMultiplier < 0) {
    return {
      federalWorkerWageLoss: 0,
      privateSectorRevenueLoss: 0,
      totalDirectEconomicCost: 0,
      gdpImpactPercentage: 0
    };
  }

  // 1. Calculate Federal Worker Wage Loss
  const dailyAvgFederalSalary = avgFederalSalary / 260; // Assuming 260 working days per year
  const numberOfAffectedWorkers = federalWorkers * affectedWorkersPercentage;
  const federalWorkerWageLoss = numberOfAffectedWorkers * dailyAvgFederalSalary * duration;

  // 2. Calculate Private Sector Revenue Loss
  const dailyGDP = annualGDP / 365;
  const dailyPrivateSectorGDPReduction = dailyGDP * (dailyPrivateSectorGDPReductionBasisPoints / 10000); // Convert basis points to percentage
  const directPrivateSectorLoss = dailyPrivateSectorGDPReduction * duration;

  // 3. Estimate Indirect Economic Cost from Consumer Confidence (Multiplier on direct losses)
  // This applies an additional layer of impact based on consumer behavior
  const indirectEconomicCost = (federalWorkerWageLoss + directPrivateSectorLoss) * consumerConfidenceMultiplier;

  // 4. Total Economic Cost
  const totalDirectEconomicCost = federalWorkerWageLoss + directPrivateSectorLoss + indirectEconomicCost;

  // 5. GDP Impact Percentage
  const gdpImpactPercentage = (totalDirectEconomicCost / annualGDP) * 100;

  return {
    federalWorkerWageLoss: federalWorkerWageLoss,
    privateSectorRevenueLoss: directPrivateSectorLoss,
    totalDirectEconomicCost: totalDirectEconomicCost,
    gdpImpactPercentage: gdpImpactPercentage
  };
}
