function (inputs) {
  const { shutdownDurationDays, federalEmployeeCountAffected, avgDailyFederalWage, dailyLostFederalContractValue, consumerConfidenceDropPercentage, dailyGDPMultiplier, avgDailyDiscretionarySpendingPerCapita, populationAffectedByConfidenceDrop } = inputs;

  // Ensure duration is at least 1 for division, or handle 0-day impact as zero
  const effectiveDuration = Math.max(1, shutdownDurationDays);

  // Step 1: Calculate total lost federal wages
  let totalLostFederalWages = shutdownDurationDays * federalEmployeeCountAffected * avgDailyFederalWage;

  // Step 2: Calculate total halted contract value
  let totalHaltedContractValue = shutdownDurationDays * dailyLostFederalContractValue;

  // Step 3: Direct economic impact from wages and contracts (multiplied)
  // This includes the immediate loss and a multiplier effect on related industries/services.
  let directEconomicBase = totalLostFederalWages + totalHaltedContractValue;
  let estimatedDirectGDPImpact = directEconomicBase * dailyGDPMultiplier;

  // Step 4: Consumer spending reduction (indirect) due to confidence drop
  // Cap confidence drop to prevent exaggerated impacts and reflect realistic consumer behavior.
  const effectiveConsumerConfidenceDropPercentage = Math.max(0, Math.min(consumerConfidenceDropPercentage, 10)); // Cap between 0% and 10%

  // This estimates the reduction in *discretionary* spending over the shutdown period.
  // We use a scaled multiplier (e.g., 0.5 of the direct GDP multiplier) because indirect confidence impact might be less immediate or linear than direct spending.
  let estimatedConsumerSpendingReduction = shutdownDurationDays * populationAffectedByConfidenceDrop * avgDailyDiscretionarySpendingPerCapita * (effectiveConsumerConfidenceDropPercentage / 100) * (dailyGDPMultiplier * 0.75); // Using a 75% scaled multiplier for indirect effect

  // Step 5: Total Estimated Economic Impact
  let totalEstimatedEconomicImpact = estimatedDirectGDPImpact + estimatedConsumerSpendingReduction;

  // Step 6: Daily Economic Impact
  let dailyEconomicImpact = totalEstimatedEconomicImpact / effectiveDuration;

  // Handle edge case where shutdownDurationDays is 0 or negative, returning all zeros
  if (shutdownDurationDays <= 0) {
    totalLostFederalWages = 0;
    totalHaltedContractValue = 0;
    estimatedDirectGDPImpact = 0;
    estimatedConsumerSpendingReduction = 0;
    totalEstimatedEconomicImpact = 0;
    dailyEconomicImpact = 0;
  }

  return {
    totalLostFederalWages: totalLostFederalWages,
    totalHaltedContractValue: totalHaltedContractValue,
    estimatedDirectGDPImpact: estimatedDirectGDPImpact,
    estimatedConsumerSpendingReduction: estimatedConsumerSpendingReduction,
    totalEstimatedEconomicImpact: totalEstimatedEconomicImpact,
    dailyEconomicImpact: dailyEconomicImpact
  };
}
