function calculate({ monthlyNetIncome, percentageFederalIncome, receivesFederalBenefits, averageMonthlyFederalBenefits, monthlyFixedExpenses, monthlyLoanPayments, emergencySavings, shutdownDurationWeeks, unemploymentWeeklyBenefit }) {
  // Input validation and normalization
  monthlyNetIncome = Math.max(0, monthlyNetIncome || 0);
  percentageFederalIncome = Math.max(0, Math.min(100, percentageFederalIncome || 0));
  averageMonthlyFederalBenefits = receivesFederalBenefits ? Math.max(0, averageMonthlyFederalBenefits || 0) : 0;
  monthlyFixedExpenses = Math.max(0, monthlyFixedExpenses || 0);
  monthlyLoanPayments = Math.max(0, monthlyLoanPayments || 0);
  emergencySavings = Math.max(0, emergencySavings || 0);
  shutdownDurationWeeks = Math.max(0, shutdownDurationWeeks || 0);
  unemploymentWeeklyBenefit = Math.max(0, unemploymentWeeklyBenefit || 0);

  if (shutdownDurationWeeks === 0) {
    return {
      totalIncomeLoss: 0,
      totalBenefitDisruption: 0,
      totalUnemploymentOffset: 0,
      totalExpensesDuringShutdown: 0,
      netFinancialImpact: 0,
      weeksUntilSavingsDepleted: 0,
      additionalFundsNeeded: 0
    };
  }

  const daysInMonth = 30.437; // Average days in a month
  const shutdownDurationDays = shutdownDurationWeeks * 7;

  // 1. Calculate Federal Income Loss
  const dailyFederalIncome = (monthlyNetIncome * (percentageFederalIncome / 100)) / daysInMonth;
  const totalIncomeLoss = dailyFederalIncome * shutdownDurationDays;

  // 2. Calculate Federal Benefit Disruption
  const dailyFederalBenefit = averageMonthlyFederalBenefits / daysInMonth;
  const totalBenefitDisruption = dailyFederalBenefit * shutdownDurationDays;

  // 3. Calculate Unemployment Benefit Offset
  const totalUnemploymentOffset = unemploymentWeeklyBenefit * shutdownDurationWeeks;

  // 4. Calculate Total Expenses During Shutdown
  const dailyTotalExpenses = (monthlyFixedExpenses + monthlyLoanPayments) / daysInMonth;
  const totalExpensesDuringShutdown = dailyTotalExpenses * shutdownDurationDays;

  // 5. Calculate Net Financial Impact (how much money is *needed* to cover expenses and lost income)
  const requiredFunds = totalIncomeLoss + totalBenefitDisruption + totalExpensesDuringShutdown - totalUnemploymentOffset;

  // Net impact before considering savings, can be negative if benefits outweigh loss/expenses (unlikely)
  // However, for this tool, we're focused on the *outflow* vs. *inflow*.
  // A more direct 'net financial impact' is the total outflow (expenses + lost income) minus total inflow (unemployment)
  const netCashOutflowDuringShutdown = totalIncomeLoss + totalBenefitDisruption + totalExpensesDuringShutdown - totalUnemploymentOffset;

  // 6. Calculate Savings Depletion
  let weeksUntilSavingsDepleted = 0;
  let additionalFundsNeeded = 0;

  if (netCashOutflowDuringShutdown > emergencySavings) {
    additionalFundsNeeded = netCashOutflowDuringShutdown - emergencySavings;
    const weeklyNetCashOutflow = netCashOutflowDuringShutdown / shutdownDurationWeeks; // This is an average weekly drain

    if (weeklyNetCashOutflow > 0) {
        // Calculate how many weeks savings would last *under this specific shutdown's conditions*
        // This assumes the *rate* of need is constant across the shutdown duration for simplicity.
        weeksUntilSavingsDepleted = emergencySavings / (netCashOutflowDuringShutdown / shutdownDurationWeeks);
        if (weeksUntilSavingsDepleted > shutdownDurationWeeks) {
             // If savings last longer than the shutdown, then no depletion is truly 'reached' by the shutdown's end.
             // This output indicates when savings would hypothetically run out *if the shutdown impact continued*.
             // For this tool, we cap it at shutdown duration if funds are sufficient.
             weeksUntilSavingsDepleted = shutdownDurationWeeks;
             additionalFundsNeeded = 0; // No additional funds needed if savings cover it
        }
    } else {
        // If weekly outflow is zero or negative (e.g., benefits cover everything), savings never deplete
        weeksUntilSavingsDepleted = shutdownDurationWeeks; // Savings last through entire shutdown
    }
  } else {
    // Savings cover the entire impact, no additional funds needed
    weeksUntilSavingsDepleted = shutdownDurationWeeks; // Savings last through the entire shutdown
    additionalFundsNeeded = 0;
  }

  // Adjust netFinancialImpact to reflect the *actual* immediate cash impact, which savings might absorb.
  // The 'netFinancialImpact' here will represent the total *cost* of the shutdown that must be covered.
  const trueNetFinancialImpact = netCashOutflowDuringShutdown;

  return {
    totalIncomeLoss: totalIncomeLoss,
    totalBenefitDisruption: totalBenefitDisruption,
    totalUnemploymentOffset: totalUnemploymentOffset,
    totalExpensesDuringShutdown: totalExpensesDuringShutdown,
    netFinancialImpact: trueNetFinancialImpact,
    weeksUntilSavingsDepleted: weeksUntilSavingsDepleted,
    additionalFundsNeeded: additionalFundsNeeded
  };
}
