function calculate() {
    const monthlyFederalNetIncome = inputs.monthlyFederalNetIncome;
    const monthlyTotalExpenses = inputs.monthlyTotalExpenses;
    const percentageEssentialExpenses = inputs.percentageEssentialExpenses / 100;
    const emergencyFundBalance = inputs.emergencyFundBalance;
    const projectedShutdownWeeks = inputs.projectedShutdownWeeks;
    const eligibleForUnemployment = inputs.eligibleForUnemployment === 'Yes';
    const monthlyOtherIncome = inputs.monthlyOtherIncome;
    const creditCardAPR = inputs.creditCardAPR / 100;

    const WEEKS_IN_MONTH = 4.33333; // Approximately 52 weeks / 12 months
    const UNEMPLOYMENT_BENEFIT_REPLACEMENT_RATE = 0.5; // Estimate 50% income replacement
    const UNEMPLOYMENT_CAP = 2500; // Example cap, typically state-dependent, per month
    const INTEREST_CALC_PERIOD_MONTHS = 6; // Assume debt is carried for 6 months for interest estimation
    const NON_ESSENTIAL_EXPENSE_REDUCTION = 0.5; // Assume 50% reduction in non-essential spending during shutdown
    const EMERGENCY_FUND_BUFFER_MULTIPLIER = 1.5; // Recommend 1.5x expenses for shutdown duration

    // Derived weekly values
    const weeklyFederalNetIncome = monthlyFederalNetIncome / WEEKS_IN_MONTH;
    const weeklyTotalExpenses = monthlyTotalExpenses / WEEKS_IN_MONTH;
    const weeklyEssentialExpenses = weeklyTotalExpenses * percentageEssentialExpenses;
    const weeklyNonEssentialExpenses = weeklyTotalExpenses * (1 - percentageEssentialExpenses);

    // 1. Calculate Total Lost Federal Income (Gross)
    let totalLostFederalIncome = weeklyFederalNetIncome * projectedShutdownWeeks;

    // Calculate total expenses during shutdown, assuming reduced non-essential spending
    const reducedNonEssentialWeeklyExpenses = weeklyNonEssentialExpenses * NON_ESSENTIAL_EXPENSE_REDUCTION;
    const totalWeeklyExpensesDuringShutdown = weeklyEssentialExpenses + reducedNonEssentialWeeklyExpenses;
    const totalCashNeededForExpenses = totalWeeklyExpensesDuringShutdown * projectedShutdownWeeks;

    // Calculate total income available from non-federal sources and unemployment
    let totalAvailableNonFederalIncome = (monthlyOtherIncome / WEEKS_IN_MONTH * projectedShutdownWeeks);
    if (eligibleForUnemployment) {
        // Calculate potential weekly unemployment, capped by monthly cap converted to weekly
        const weeklyUnemploymentCap = UNEMPLOYMENT_CAP / WEEKS_IN_MONTH;
        const potentialUnemploymentWeekly = Math.min(weeklyFederalNetIncome * UNEMPLOYMENT_BENEFIT_REPLACEMENT_RATE, weeklyUnemploymentCap);
        totalAvailableNonFederalIncome += (potentialUnemploymentWeekly * projectedShutdownWeeks);
    }

    // Calculate remaining cash needed after other income
    let remainingCashNeededAfterOtherIncome = Math.max(0, totalCashNeededForExpenses - totalAvailableNonFederalIncome);

    // 2. Emergency Fund Depletion
    let weeksUntilEmergencyFundDepleted = 0;
    if (emergencyFundBalance > 0 && totalWeeklyExpensesDuringShutdown > 0) {
        weeksUntilEmergencyFundDepleted = emergencyFundBalance / totalWeeklyExpensesDuringShutdown;
    }
    weeksUntilEmergencyFundDepleted = Math.min(weeksUntilEmergencyFundDepleted, projectedShutdownWeeks); // Cap at shutdown duration

    // 3. Projected Debt Incurred
    let projectedDebtFromShutdown = Math.max(0, remainingCashNeededAfterOtherIncome - emergencyFundBalance);

    // 4. Net Financial Impact (Total Lost Income + Debt)
    // This represents the total financial 'hole' including gross lost wages and new debt.
    let netFinancialImpact = totalLostFederalIncome + projectedDebtFromShutdown;

    // 5. Estimated Credit Card Interest on New Debt
    let estimatedCreditCardInterest = 0;
    if (projectedDebtFromShutdown > 0 && creditCardAPR > 0) {
        estimatedCreditCardInterest = projectedDebtFromShutdown * creditCardAPR * (INTEREST_CALC_PERIOD_MONTHS / 12);
    }

    // 6. Recommended Emergency Fund Goal
    // Goal is 1.5x the total essential + reduced non-essential expenses for the projected duration.
    const recommendedEmergencyFundGoal = totalCashNeededForExpenses * EMERGENCY_FUND_BUFFER_MULTIPLIER;

    return {
        totalLostFederalIncome: totalLostFederalIncome,
        netFinancialImpact: netFinancialImpact,
        weeksUntilEmergencyFundDepleted: weeksUntilEmergencyFundDepleted,
        projectedDebtFromShutdown: projectedDebtFromShutdown,
        estimatedCreditCardInterest: estimatedCreditCardInterest,
        recommendedEmergencyFundGoal: recommendedEmergencyFundGoal
    };
}
