const gradeMap = {
    'A': 4.0, 'A-': 3.7,
    'B+': 3.3, 'B': 3.0, 'B-': 2.7,
    'C+': 2.3, 'C': 2.0, 'C-': 1.7,
    'D+': 1.3, 'D': 1.0, 'D-': 0.7,
    'F': 0.0
};

let semCredits = 0;
let semPoints = 0;

// Loop through possible 5 classes
for (let i = 1; i <= 5; i++) {
    const c = parseFloat(inputs[`credits${i}`]);
    const g = inputs[`grade${i}`];
    if (c > 0 && g && gradeMap[g] !== undefined) {
        semCredits += c;
        semPoints += (c * gradeMap[g]);
    }
}

let semGPA = 0;
if (semCredits > 0) semGPA = semPoints / semCredits;

// Cumulative logic
const prevGPA = parseFloat(inputs.previousCUMGPA) || 0;
const prevCredits = parseFloat(inputs.previousCUMCredits) || 0;

const totalPoints = (prevGPA * prevCredits) + semPoints;
const totalCredits = prevCredits + semCredits;

let cumGPA = 0;
if (totalCredits > 0) cumGPA = totalPoints / totalCredits;

return {
    semesterGPA: semGPA,
    cumulativeGPA: cumGPA,
    totalCredits: totalCredits
};
