const gradeMap = {
    'A+': 4.0, 'A': 4.0, 'A-': 3.7,
    'B+': 3.3, 'B': 3.0, 'B-': 2.7,
    'C+': 2.3, 'C': 2.0, 'C-': 1.7,
    'D+': 1.3, 'D': 1.0, 'D-': 0.7,
    'F': 0.0
};

let currentSemesterCredits = 0;
let currentSemesterGradePoints = 0;

// Process Course 1
const credits1 = parseFloat(inputs.credits1 || 0);
const grade1 = inputs.grade1 || '';
if (credits1 > 0 && gradeMap.hasOwnProperty(grade1.toUpperCase().trim())) {
    currentSemesterCredits += credits1;
    currentSemesterGradePoints += credits1 * gradeMap[grade1.toUpperCase().trim()];
}

// Process Course 2
const credits2 = parseFloat(inputs.credits2 || 0);
const grade2 = inputs.grade2 || '';
if (credits2 > 0 && gradeMap.hasOwnProperty(grade2.toUpperCase().trim())) {
    currentSemesterCredits += credits2;
    currentSemesterGradePoints += credits2 * gradeMap[grade2.toUpperCase().trim()];
}

// Process Course 3
const credits3 = parseFloat(inputs.credits3 || 0);
const grade3 = inputs.grade3 || '';
if (credits3 > 0 && gradeMap.hasOwnProperty(grade3.toUpperCase().trim())) {
    currentSemesterCredits += credits3;
    currentSemesterGradePoints += credits3 * gradeMap[grade3.toUpperCase().trim()];
}

let semesterGPA = 0;
if (currentSemesterCredits > 0) {
    semesterGPA = currentSemesterGradePoints / currentSemesterCredits;
} else if (currentSemesterCredits === 0) {
    // If no new courses or invalid inputs, semester GPA is 0
    semesterGPA = 0;
}

const previousCUMGPA = parseFloat(inputs.previousCUMGPA || 0);
const previousCUMCredits = parseFloat(inputs.previousCUMCredits || 0);

const totalCUMCredits = previousCUMCredits + currentSemesterCredits;
const totalCUMGradePoints = (previousCUMGPA * previousCUMCredits) + currentSemesterGradePoints;

let cumulativeGPA = 0;
if (totalCUMCredits > 0) {
    cumulativeGPA = totalCUMGradePoints / totalCUMCredits;
} else if (previousCUMCredits > 0) {
    // If no new courses, but previous cumulative data exists
    cumulativeGPA = previousCUMGPA;
}

return {
    semesterGPA: semesterGPA,
    cumulativeGPA: cumulativeGPA,
    totalCredits: totalCUMCredits
};
