function calculate() {
    const currentGpa = parseFloat(inputs.currentGpa);
    const currentCredits = parseFloat(inputs.currentCredits);

    let newGradePoints = 0;
    let newCredits = 0;

    // Process Course 1 (required)
    const course1Grade = parseFloat(inputs.course1Grade);
    const course1Credits = parseFloat(inputs.course1Credits);
    if (!isNaN(course1Grade) && !isNaN(course1Credits)) {
        newGradePoints += course1Grade * course1Credits;
        newCredits += course1Credits;
    }

    // Process Course 2 (optional)
    if (inputs.course2Grade && inputs.course2Credits && !isNaN(parseFloat(inputs.course2Grade)) && !isNaN(parseFloat(inputs.course2Credits))) {
        const course2Grade = parseFloat(inputs.course2Grade);
        const course2Credits = parseFloat(inputs.course2Credits);
        newGradePoints += course2Grade * course2Credits;
        newCredits += course2Credits;
    }

    // Process Course 3 (optional)
    if (inputs.course3Grade && inputs.course3Credits && !isNaN(parseFloat(inputs.course3Grade)) && !isNaN(parseFloat(inputs.course3Credits))) {
        const course3Grade = parseFloat(inputs.course3Grade);
        const course3Credits = parseFloat(inputs.course3Credits);
        newGradePoints += course3Grade * course3Credits;
        newCredits += course3Credits;
    }

    const currentGradePoints = currentGpa * currentCredits;
    const totalCredits = currentCredits + newCredits;
    const totalGradePoints = currentGradePoints + newGradePoints;

    let projectedGpa = 0;
    if (totalCredits > 0) {
        projectedGpa = totalGradePoints / totalCredits;
    }

    // Cap GPA at 4.0 if it exceeds (e.g., due to unusual institutional grading or input errors, though unlikely with standard inputs)
    projectedGpa = Math.min(projectedGpa, 4.0);
    // Ensure GPA doesn't go below 0 (though also unlikely with standard inputs)
    projectedGpa = Math.max(projectedGpa, 0.0);

    return {
        projectedGpa: projectedGpa,
        termGradePoints: newGradePoints,
        totalCredits: totalCredits
    };
}
