const upfrontGreenCost = inputs.upfrontGreenCost;
const upfrontTraditionalCost = inputs.upfrontTraditionalCost;
const annualEnergyReductionPercentage = inputs.annualEnergyReductionPercentage;
const baselineAnnualEnergyCost = inputs.baselineAnnualEnergyCost;
const annualMaintenanceReductionPercentage = inputs.annualMaintenanceReductionPercentage;
const baselineAnnualMaintenanceCost = inputs.baselineAnnualMaintenanceCost;
const totalRebatesIncentives = inputs.totalRebatesIncentives;
const certificationPremiumValue = inputs.certificationPremiumValue;
const projectEvaluationPeriodYears = inputs.projectEvaluationPeriodYears;

let netUpfrontInvestment = 0;
let annualOperatingSavings = 0;
let totalMonetaryBenefits = 0;
let simpleROI = 0;
let paybackPeriodYears = 0;

// Basic validation for evaluation period
if (projectEvaluationPeriodYears <= 0) {
    return {
        netUpfrontInvestment: 0,
        annualOperatingSavings: 0,
        totalMonetaryBenefits: 0,
        simpleROI: 0,
        paybackPeriodYears: 0,
        errorMessage: "Project Evaluation Period must be greater than 0."
    };
}

// 1. Calculate Gross Upfront Cost Difference
const grossUpfrontCostDifference = upfrontGreenCost - upfrontTraditionalCost;

// 2. Calculate Net Upfront Investment (after incentives)
netUpfrontInvestment = grossUpfrontCostDifference - totalRebatesIncentives;

// 3. Calculate Annual Operating Savings
const annualEnergySavings = baselineAnnualEnergyCost * (annualEnergyReductionPercentage / 100);
const annualMaintenanceSavings = baselineAnnualMaintenanceCost * (annualMaintenanceReductionPercentage / 100);
annualOperatingSavings = annualEnergySavings + annualMaintenanceSavings;

// 4. Calculate Total Monetary Benefits Over Evaluation Period
const totalOperatingSavingsOverPeriod = annualOperatingSavings * projectEvaluationPeriodYears;
// Certification premium value is a one-time uplift, added to the total benefits.
totalMonetaryBenefits = totalOperatingSavingsOverPeriod + certificationPremiumValue;

// 5. Calculate Simple ROI and Payback Period
if (netUpfrontInvestment <= 0) {
    // If green materials are cheaper or equal after incentives, it's immediately beneficial.
    // No additional investment means ROI is effectively infinite, and payback is instantaneous.
    simpleROI = 999999999; // Representing a very high, immediate return
    paybackPeriodYears = 0;
} else {
    const netFinancialGain = totalMonetaryBenefits - netUpfrontInvestment;
    if (netFinancialGain <= 0) {
        simpleROI = -100; // Represents a net loss or zero gain over the period
    } else {
        simpleROI = (netFinancialGain / netUpfrontInvestment) * 100;
    }

    if (annualOperatingSavings <= 0) {
        paybackPeriodYears = Infinity; // No operational savings means no payback from operations.
    } else {
        paybackPeriodYears = netUpfrontInvestment / annualOperatingSavings;
    }
}

return {
    netUpfrontInvestment: netUpfrontInvestment,
    annualOperatingSavings: annualOperatingSavings,
    totalMonetaryBenefits: totalMonetaryBenefits,
    simpleROI: simpleROI,
    paybackPeriodYears: paybackPeriodYears
};
