const { greenhouse_length_ft, greenhouse_width_ft, wall_height_ft, peak_height_ft, desired_internal_temp_f, average_external_temp_f, u_value_btu_ft2_hr_f, heater_efficiency_percent, energy_cost_per_kwh } = inputs;

const BTU_PER_KWH = 3412.14;
const HOURS_PER_DAY = 24;
const AVG_DAYS_PER_MONTH = 30.44;

// Input Validation
if (greenhouse_length_ft <= 0 || greenhouse_width_ft <= 0 || wall_height_ft <= 0) {
    throw new Error('Greenhouse dimensions must be positive values.');
}
if (peak_height_ft < wall_height_ft) {
    throw new Error('Roof Peak Height cannot be less than Side Wall Height.');
}
if (u_value_btu_ft2_hr_f <= 0) {
    throw new Error('U-Value must be a positive value.');
}
if (heater_efficiency_percent <= 0 || heater_efficiency_percent > 100) {
    throw new Error('Heater efficiency must be between 1% and 100%.');
}
if (energy_cost_per_kwh < 0) {
    throw new Error('Energy cost cannot be negative.');
}

// 1. Calculate Temperature Difference
const delta_temp_f = desired_internal_temp_f - average_external_temp_f;

// If internal temp is not higher than external, no heating is needed
if (delta_temp_f <= 0) {
  return {
    daily_heating_cost: 0,
    monthly_heating_cost: 0,
    daily_energy_consumption_kwh: 0,
    monthly_energy_consumption_kwh: 0,
  };
}

// 2. Calculate Surface Area (A) - assuming a simple gable roof
const side_wall_area = 2 * (greenhouse_length_ft * wall_height_ft) + 2 * (greenhouse_width_ft * wall_height_ft);

const rise_ft = peak_height_ft - wall_height_ft;
const half_width_ft = greenhouse_width_ft / 2;
// Calculate the slope length of the roof section using Pythagorean theorem
const slope_length_ft = Math.sqrt(half_width_ft * half_width_ft + rise_ft * rise_ft);
const roof_area = 2 * greenhouse_length_ft * slope_length_ft;

const total_exposed_area_ft2 = side_wall_area + roof_area;

// 3. Calculate Hourly Heat Loss (Q_hourly_btu)
const q_hourly_btu = u_value_btu_ft2_hr_f * total_exposed_area_ft2 * delta_temp_f;

// 4. Calculate Heater Efficiency (decimal)
// Cap efficiency at 100% (1.0) for calculations and ensure a minimum value (e.g., 1%) to prevent division by zero
const effective_heater_efficiency = Math.min(1.0, Math.max(0.01, heater_efficiency_percent / 100));

// 5. Calculate Hourly Energy Consumption (in BTU and then kWh)
const hourly_energy_btu = q_hourly_btu / effective_heater_efficiency;
const hourly_energy_kwh = hourly_energy_btu / BTU_PER_KWH;

// 6. Calculate Daily and Monthly Energy Consumption
const daily_energy_consumption_kwh = hourly_energy_kwh * HOURS_PER_DAY;
const monthly_energy_consumption_kwh = hourly_energy_kwh * HOURS_PER_DAY * AVG_DAYS_PER_MONTH;

// 7. Calculate Daily and Monthly Heating Costs
const daily_heating_cost = daily_energy_consumption_kwh * energy_cost_per_kwh;
const monthly_heating_cost = monthly_energy_consumption_kwh * energy_cost_per_kwh;

return {
  daily_heating_cost: daily_heating_cost,
  monthly_heating_cost: monthly_heating_cost,
  daily_energy_consumption_kwh: daily_energy_consumption_kwh,
  monthly_energy_consumption_kwh: monthly_energy_consumption_kwh,
};
