function calculateGEC(inputs) {
  const { service_conductor_material, service_conductor_size, grounding_electrode_type, is_gec_exposed_to_physical_damage } = inputs;

  // GEC sizes ordered from largest (index 0) to smallest
  const gecCopperSizes = [
    "3/0 AWG", "2/0 AWG", "1/0 AWG", "1 AWG", 
    "2 AWG", "4 AWG", "6 AWG", "8 AWG"
  ];
  const gecAluminumSizes = [
    "250 kcmil", "4/0 AWG", "3/0 AWG", "2/0 AWG", 
    "1/0 AWG", "1 AWG", "2 AWG", "4 AWG", "6 AWG"
  ];

  // Function to get the index of a GEC size (lower index = larger conductor)
  const getGECIndex = (size, material) => {
    const list = material === 'copper' ? gecCopperSizes : gecAluminumSizes;
    return list.indexOf(size);
  };

  // Function to get the GEC size string from an index
  const getGECSizeString = (index, material) => {
    const list = material === 'copper' ? gecCopperSizes : gecAluminumSizes;
    return index >= 0 && index < list.length ? list[index] : 'N/A';
  };

  // Base GEC sizes based on NEC Table 250.66 for service conductors
  // Values are indices into gecCopperSizes and gecAluminumSizes lists
  const baseGECMapping = {
    'awg_2_or_smaller': { cu: getGECIndex('8 AWG', 'copper'), al: getGECIndex('6 AWG', 'aluminum') },
    'awg_1_to_1/0': { cu: getGECIndex('6 AWG', 'copper'), al: getGECIndex('4 AWG', 'aluminum') },
    'awg_2/0_to_3/0': { cu: getGECIndex('4 AWG', 'copper'), al: getGECIndex('2 AWG', 'aluminum') },
    'kcmil_250_to_350': { cu: getGECIndex('2 AWG', 'copper'), al: getGECIndex('1/0 AWG', 'aluminum') },
    'kcmil_400_to_600': { cu: getGECIndex('1/0 AWG', 'copper'), al: getGECIndex('3/0 AWG', 'aluminum') },
    'kcmil_600_to_1100': { cu: getGECIndex('2/0 AWG', 'copper'), al: getGECIndex('4/0 AWG', 'aluminum') },
    'kcmil_over_1100': { cu: getGECIndex('3/0 AWG', 'copper'), al: getGECIndex('250 kcmil', 'aluminum') } // For informational purposes, use common largest sizes, or 250.66(A) rule
  };

  let gecCuIndex = baseGECMapping[service_conductor_size].cu;
  let gecAlIndex = baseGECMapping[service_conductor_size].al;

  // Apply electrode type limitations (NEC 250.66 Notes 1 and 2)
  if (grounding_electrode_type === 'rod_plate') {
    // Not required to be larger than #6 AWG copper or #4 AWG aluminum
    gecCuIndex = Math.max(gecCuIndex, getGECIndex('6 AWG', 'copper'));
    gecAlIndex = Math.max(gecAlIndex, getGECIndex('4 AWG', 'aluminum'));
  } else if (grounding_electrode_type === 'ufer') {
    // Not required to be larger than #4 AWG copper or #2 AWG aluminum
    gecCuIndex = Math.max(gecCuIndex, getGECIndex('4 AWG', 'copper'));
    gecAlIndex = Math.max(gecAlIndex, getGECIndex('2 AWG', 'aluminum'));
  }

  let protectionRecommendation = "No specific protection requirement noted.";

  // Apply physical damage rule (NEC 250.64(B))
  if (is_gec_exposed_to_physical_damage) {
    // GEC #6 AWG and smaller must be protected or run in rigid nonmetallic conduit
    // For this calculator, if GEC is #6 AWG copper or #4 AWG aluminum (or smaller), it needs protection.
    const minProtectedCuIndex = getGECIndex('6 AWG', 'copper');
    const minProtectedAlIndex = getGECIndex('4 AWG', 'aluminum');

    if (gecCuIndex >= minProtectedCuIndex || gecAlIndex >= minProtectedAlIndex) {
        protectionRecommendation = "Protection required where exposed to physical damage.";
    } else {
        protectionRecommendation = "Protection recommended if GEC is #6 AWG Copper or #4 AWG Aluminum or smaller.";
    }

    // If the GEC calculation results in a size smaller than #6 copper or #4 aluminum, it doesn't mean it should be sized up for protection, but rather that it needs to be protected if installed unprotected.
    // The rule primarily dictates protection, not upsizing the conductor beyond 250.66 based on exposure.
    // A #8 GEC can be installed if protected. The note specifies #6 or smaller needs protection.
    // The code does not say 'if exposed, size up to #6'. It says 'if exposed without protection, it shall not be smaller than #6 AWG'.
    // This implies that if your calculation yields #8, and it's exposed, you either protect it, or if you don't, then you must use #6.
    // For simplicity, we'll advise protection, and only enforce the *minimum* size if it's currently smaller.
    // The original GEC size from 250.66 is the primary. If it results in a smaller wire, then protection is mandatory or upsizing to #6 (Cu) / #4 (Al) if no protection.

    // Let's implement this as: if the calculated size is smaller than the minimum for unprotected, then it implies protection is needed, or the size must be increased.
    const defaultMinUnprotectedCu = getGECIndex('6 AWG', 'copper'); // #6 AWG Cu
    const defaultMinUnprotectedAl = getGECIndex('4 AWG', 'aluminum'); // #4 AWG Al

    if (gecCuIndex > defaultMinUnprotectedCu) { // If calculated CU GEC is smaller than #6
        protectionRecommendation = "Protection required (GEC is smaller than #6 AWG Copper). If unprotected, must be #6 AWG Copper or larger.";
    } else if (gecAlIndex > defaultMinUnprotectedAl) { // If calculated AL GEC is smaller than #4
        protectionRecommendation = "Protection required (GEC is smaller than #4 AWG Aluminum). If unprotected, must be #4 AWG Aluminum or larger.";
    } else {
        protectionRecommendation = "Protection required for GEC exposed to physical damage. Current size meets minimum for unprotected if larger than #6 Cu / #4 Al, but local conditions may vary.";
    }
  }

  // Output the final GEC sizes
  const finalGecCuSize = getGECSizeString(gecCuIndex, 'copper');
  const finalGecAlSize = getGECSizeString(gecAlIndex, 'aluminum');

  return {
    gec_copper_size: finalGecCuSize,
    gec_aluminum_size: finalGecAlSize,
    gec_protection_recommendation: protectionRecommendation
  };
}
