function (inputs) {
    const { currentAnnualSalary, currentCityColIndex, currentStateTaxRate, targetCityColIndex, targetStateTaxRate, h1bVisaFee, legalFees, relocationExpenses, dependents } = inputs;

    // Input Validation and Defaults
    if (currentAnnualSalary <= 0 || currentCityColIndex <= 0 || targetCityColIndex <= 0) {
        return {
            currentNetIncomeAfterStateTax: 0,
            annualNetIncomeChange: 0,
            equivalentSalaryNeeded: 0,
            totalOneTimeCosts: 0,
            overallFinancialImpactYear1: 0
        };
    }

    const effectiveCurrentStateTaxRate = Math.max(0, Math.min(currentStateTaxRate, 100)) / 100;
    const effectiveTargetStateTaxRate = Math.max(0, Math.min(targetStateTaxRate, 100)) / 100;

    const colFactor = targetCityColIndex / currentCityColIndex;

    // 1. Calculate Current Net Income (after state tax)
    const currentNetIncomeAfterStateTax = currentAnnualSalary * (1 - effectiveCurrentStateTaxRate);

    // 2. Calculate current purchasing power parity (relative to a base COL of 1)
    const currentPurchasingPowerParity = currentNetIncomeAfterStateTax / (currentCityColIndex / 100);

    // 3. Calculate Target Net Income (after state tax, before COL adjustment)
    const targetNetIncomeBeforeCOLAdjust = currentAnnualSalary * (1 - effectiveTargetStateTaxRate);

    // 4. Calculate adjusted purchasing power in the target location
    const targetAdjustedPurchasingPower = targetNetIncomeBeforeCOLAdjust / (targetCityColIndex / 100);

    // 5. Calculate Annual Net Income Change (after tax & COL)
    const annualNetIncomeChange = targetAdjustedPurchasingPower - currentPurchasingPowerParity;

    // 6. Calculate Equivalent Gross Salary Needed in Target Location
    // What gross salary in the new location, after new state tax, would yield the same purchasing power
    // as the current gross salary after current state tax and current COL?
    // newGrossSalary * (1 - targetStateTaxRate) / (targetCityColIndex / 100) = currentPurchasingPowerParity
    // newGrossSalary = currentPurchasingPowerParity * (targetCityColIndex / 100) / (1 - targetStateTaxRate)
    const equivalentSalaryNeeded = currentPurchasingPowerParity * (targetCityColIndex / 100) / (1.00000001 - effectiveTargetStateTaxRate); // Added a small epsilon to prevent division by zero if targetStateTaxRate is 100%

    // Ensure equivalentSalaryNeeded is not negative if state tax makes it impossible
    const finalEquivalentSalaryNeeded = equivalentSalaryNeeded < 0 ? 0 : equivalentSalaryNeeded;

    // 7. Calculate Total One-Time Costs (including nominal dependent fees)
    const dependentVisaFeeEstimate = dependents * 500; // A nominal estimate for additional dependent costs
    const totalOneTimeCosts = h1bVisaFee + legalFees + relocationExpenses + dependentVisaFeeEstimate;

    // 8. Calculate Overall Financial Impact (Year 1)
    const overallFinancialImpactYear1 = annualNetIncomeChange - totalOneTimeCosts;

    return {
        currentNetIncomeAfterStateTax: currentNetIncomeAfterStateTax,
        annualNetIncomeChange: annualNetIncomeChange,
        equivalentSalaryNeeded: finalEquivalentSalaryNeeded,
        totalOneTimeCosts: totalOneTimeCosts,
        overallFinancialImpactYear1: overallFinancialImpactYear1
    };
}
