function calculate({ initialInvestment, currentInfectionRate, expectedReductionInfectionRate, avgTreatmentCostPerInfection, annualPatientDays, annualMaintenanceCostIAQ }) {
    // Ensure inputs are numbers and handle potential non-positive values gracefully
    initialInvestment = Math.max(0, parseFloat(initialInvestment) || 0);
    currentInfectionRate = Math.max(0, parseFloat(currentInfectionRate) || 0);
    expectedReductionInfectionRate = Math.max(0, Math.min(100, parseFloat(expectedReductionInfectionRate) || 0)); // Cap reduction at 100%
    avgTreatmentCostPerInfection = Math.max(0, parseFloat(avgTreatmentCostPerInfection) || 0);
    annualPatientDays = Math.max(0, parseFloat(annualPatientDays) || 0);
    annualMaintenanceCostIAQ = Math.max(0, parseFloat(annualMaintenanceCostIAQ) || 0);

    // Calculate the number of infections prevented annually
    // currentInfectionRate is per 1000 patient-days, so divide by 1000
    // expectedReductionInfectionRate is a percentage, so divide by 100
    const infectionsPreventedPerYear = (currentInfectionRate / 1000) * (expectedReductionInfectionRate / 100) * annualPatientDays;

    // Calculate gross annual savings from reduced infections
    const grossAnnualSavings = infectionsPreventedPerYear * avgTreatmentCostPerInfection;

    // Calculate net annual savings after deducting annual maintenance costs
    const netAnnualSavings = Math.max(0, grossAnnualSavings - annualMaintenanceCostIAQ); // Savings cannot be negative

    // Calculate ROI percentage
    let roiPercentage = 0;
    if (initialInvestment > 0) {
        roiPercentage = (netAnnualSavings / initialInvestment) * 100;
    }

    // Calculate Payback Period
    let paybackPeriodYears = Infinity; // Default to infinity if no savings or investment
    if (netAnnualSavings > 0 && initialInvestment > 0) {
        paybackPeriodYears = initialInvestment / netAnnualSavings;
    } else if (initialInvestment === 0 && netAnnualSavings > 0) {
        paybackPeriodYears = 0; // If no investment, and there are savings, payback is immediate.
    } else if (initialInvestment === 0 && netAnnualSavings === 0) {
        paybackPeriodYears = 0;
    } else if (initialInvestment > 0 && netAnnualSavings <= 0) {
        paybackPeriodYears = Infinity; // No payback if no net savings or negative net savings
    }

    return {
        annualSavingsFromInfections: netAnnualSavings,
        roiPercentage: roiPercentage,
        paybackPeriodYears: paybackPeriodYears
    };
}
