function calculate(inputs) {
    const { 
        avgDailyFTEShortfall, 
        durationDays, 
        avgDailyCompensationFTE, 
        gapCoverageCostMultiplier, 
        patientServiceCapacityReduction, 
        avgNetRevenuePerPatientVisit, 
        normalDailyPatientVolume, 
        patientAttritionRate, 
        FTEsRequiringPermanentReplacement, 
        costToRecruitOnboardOneFTE 
    } = inputs;

    // Input validation and sanitization for non-negative values
    const safeAvgDailyFTEShortfall = Math.max(0, avgDailyFTEShortfall || 0);
    const safeDurationDays = Math.max(0, durationDays || 0);
    const safeAvgDailyCompensationFTE = Math.max(0, avgDailyCompensationFTE || 0);
    const safeGapCoverageCostMultiplier = Math.max(1, gapCoverageCostMultiplier || 1); // Multiplier should be >= 1 to reflect increased cost
    const safePatientServiceCapacityReduction = Math.min(100, Math.max(0, patientServiceCapacityReduction || 0)) / 100; // as a decimal between 0 and 1
    const safeAvgNetRevenuePerPatientVisit = Math.max(0, avgNetRevenuePerPatientVisit || 0);
    const safeNormalDailyPatientVolume = Math.max(0, normalDailyPatientVolume || 0);
    const safePatientAttritionRate = Math.min(100, Math.max(0, patientAttritionRate || 0)) / 100; // as a decimal between 0 and 1
    const safeFTEsRequiringPermanentReplacement = Math.max(0, FTEsRequiringPermanentReplacement || 0);
    const safeCostToRecruitOnboardOneFTE = Math.max(0, costToRecruitOnboardOneFTE || 0);

    // --- 1. Calculate Total Gap Coverage Costs (Direct Operational Costs) ---
    // This quantifies the immediate additional expenses incurred to cover the staffing deficit.
    // This can include higher rates for temporary staff, overtime for existing employees, or other short-term measures.
    const dailyGapCoverageCost = safeAvgDailyFTEShortfall * safeAvgDailyCompensationFTE * safeGapCoverageCostMultiplier;
    const totalGapCoverageCosts = dailyGapCoverageCost * safeDurationDays;

    // --- 2. Calculate Total Lost Revenue Impact ---
    // This accounts for the financial losses due to reduced patient capacity and patient attrition.

    // Calculate daily patients impacted by the reduced service capacity
    const dailyPatientsImpactedByCapacity = safeNormalDailyPatientVolume * safePatientServiceCapacityReduction;

    // Calculate total patients impacted over the duration of the staffing gap
    const totalPatientsImpactedOverDuration = dailyPatientsImpactedByCapacity * safeDurationDays;

    // Calculate the number of patients permanently lost due to service impact and dissatisfaction
    const totalLostPatients = totalPatientsImpactedOverDuration * safePatientAttritionRate;

    // Calculate the total revenue lost from these permanently lost patients
    const totalLostRevenueImpact = totalLostPatients * safeAvgNetRevenuePerPatientVisit;

    // --- 3. Calculate Total Recruitment & Onboarding Costs (Long-term Staffing) ---
    // These are the expenses associated with permanently filling the vacant positions that have arisen
    // or been exacerbated by the staffing gap (e.g., due to high turnover or post-strike attrition).
    const totalRecruitmentOnboardingCosts = safeFTEsRequiringPermanentReplacement * safeCostToRecruitOnboardOneFTE;

    // --- 4. Calculate Overall Total Staffing Gap Cost ---
    // This is the comprehensive sum of all calculated financial impacts.
    const overallTotalCost = totalGapCoverageCosts + totalLostRevenueImpact + totalRecruitmentOnboardingCosts;

    return {
        totalGapCoverageCosts,
        totalLostRevenueImpact,
        totalRecruitmentOnboardingCosts,
        overallTotalCost
    };
}
