formula:
function calculate({ averageOvertimePayRate, averageOvertimeHoursPerMonth, agencyStaffHourlyRate, agencyStaffHoursPerMonth, averageRecruitmentCostPerHire, totalFTEPositions, monthlyStaffTurnoverRate, currentOpenFTEPositions, averageTimeUntilFilledMonths, estimatedRevenueLossPerOpenFTEPerMonth }) {

  // Input validation and sanitization
  averageOvertimePayRate = Math.max(0, averageOvertimePayRate || 0);
  averageOvertimeHoursPerMonth = Math.max(0, averageOvertimeHoursPerMonth || 0);
  agencyStaffHourlyRate = Math.max(0, agencyStaffHourlyRate || 0);
  agencyStaffHoursPerMonth = Math.max(0, agencyStaffHoursPerMonth || 0);
  averageRecruitmentCostPerHire = Math.max(0, averageRecruitmentCostPerHire || 0);
  totalFTEPositions = Math.max(0, totalFTEPositions || 0);
  monthlyStaffTurnoverRate = Math.max(0, Math.min(100, monthlyStaffTurnoverRate || 0)); // Cap between 0 and 100
  currentOpenFTEPositions = Math.max(0, currentOpenFTEPositions || 0);
  averageTimeUntilFilledMonths = Math.max(0.1, averageTimeUntilFilledMonths || 0.1); // Ensure minimum 0.1 to avoid division by zero
  estimatedRevenueLossPerOpenFTEPerMonth = Math.max(0, estimatedRevenueLossPerOpenFTEPerMonth || 0);

  // 1. Calculate Total Monthly Overtime Cost
  const totalMonthlyOvertimeCost = averageOvertimePayRate * averageOvertimeHoursPerMonth;

  // 2. Calculate Total Monthly Agency Staff Cost
  const totalMonthlyAgencyCost = agencyStaffHourlyRate * agencyStaffHoursPerMonth;

  // 3. Calculate Estimated Monthly Recruitment Cost
  // Recruitment for monthly turnover
  const decimalTurnoverRate = monthlyStaffTurnoverRate / 100;
  const monthlyHiresToReplaceTurnover = totalFTEPositions * decimalTurnoverRate;
  const recruitmentCostForTurnover = monthlyHiresToReplaceTurnover * averageRecruitmentCostPerHire;

  // Recruitment cost amortized for existing open positions (spread over time to fill)
  const recruitmentCostForCurrentVacanciesAmortized = (currentOpenFTEPositions * averageRecruitmentCostPerHire) / averageTimeUntilFilledMonths;

  const estimatedMonthlyRecruitmentCost = recruitmentCostForTurnover + recruitmentCostForCurrentVacanciesAmortized;

  // 4. Calculate Estimated Monthly Revenue Loss
  const estimatedMonthlyRevenueLoss = currentOpenFTEPositions * estimatedRevenueLossPerOpenFTEPerMonth;

  // 5. Calculate Total Monthly Staffing Shortage Cost
  const totalMonthlyStaffingShortageCost = totalMonthlyOvertimeCost + totalMonthlyAgencyCost + estimatedMonthlyRecruitmentCost + estimatedMonthlyRevenueLoss;

  // 6. Calculate Annualized Staffing Shortage Cost
  const annualizedStaffingShortageCost = totalMonthlyStaffingShortageCost * 12;

  return {
    totalMonthlyOvertimeCost,
    totalMonthlyAgencyCost,
    estimatedMonthlyRecruitmentCost,
    estimatedMonthlyRevenueLoss,
    totalMonthlyStaffingShortageCost,
    annualizedStaffingShortageCost
  };
}
