const dietScore = inputs.dietQuality * 4; 
const totalExerciseMinutes = inputs.modExerciseMinutes + inputs.vigExerciseMinutes * 2; 
const maxExerciseContributionMinutes = 450; 
const exerciseScore = Math.min(totalExerciseMinutes, maxExerciseContributionMinutes) / maxExerciseContributionMinutes * 20; 

let sleepScore = 15; 
if (inputs.avgSleepHours < 7) { 
    sleepScore -= Math.pow(7 - inputs.avgSleepHours, 1.8) * 3; 
} else if (inputs.avgSleepHours > 9) { 
    sleepScore -= Math.pow(inputs.avgSleepHours - 9, 1.5) * 2; 
} 
sleepScore = Math.max(0, sleepScore); 

const socialScore = Math.min(inputs.socialDays, 5) * 2; 

const stressScore = inputs.stressCoping * 3; 

let totalScore = dietScore + exerciseScore + sleepScore + socialScore + stressScore; 

const smokingPenalty = inputs.smokingStatus === 1 ? 20 : 0; 
const alcoholPenalty = Math.max(0, (inputs.alcoholDrinksWeekly - 7)) * 2.5; 

totalScore = totalScore - smokingPenalty - alcoholPenalty; 

const healthyAgingScore = Math.max(0, Math.min(100, totalScore)); 

let recommendationLevel; 
if (healthyAgingScore >= 85) { 
    recommendationLevel = "Excellent: Your lifestyle strongly supports healthy aging!"; 
} else if (healthyAgingScore >= 70) { 
    recommendationLevel = "Very Good: You have many positive lifestyle factors. Keep it up!"; 
} else if (healthyAgingScore >= 50) { 
    recommendationLevel = "Good: A solid foundation, but there's room for improvement in some areas."; 
} else if (healthyAgingScore >= 30) { 
    recommendationLevel = "Needs Improvement: Consider making significant lifestyle changes to boost your healthy aging potential."; 
} else { 
    recommendationLevel = "Poor: Your current lifestyle may negatively impact your healthy aging potential. Seek professional guidance."; 
} 

return { 
    healthyAgingScore: parseFloat(healthyAgingScore.toFixed(1)), 
    lifestyleRecommendation: recommendationLevel 
};
