const { amountDrawn, interestRate, drawPeriod, repaymentPeriod } = inputs;

if (amountDrawn <= 0 || interestRate <= 0 || drawPeriod <= 0 || repaymentPeriod <= 0) {
  return {
    interestOnlyPayment: 0,
    principalAndInterestPayment: 0,
    totalInterestPaid: 0,
    totalPayments: 0
  };
}

const monthlyRate = interestRate / 100 / 12;
const drawMonths = drawPeriod * 12;
const repaymentMonths = repaymentPeriod * 12;

// 1. Calculate the payment during the draw period (interest-only)
const interestOnlyPayment = amountDrawn * monthlyRate;

// 2. Calculate the fully amortized payment during the repayment period
const P = amountDrawn; // Principal is the amount drawn at the end of the draw period
const r = monthlyRate;
const n = repaymentMonths;
const numerator = r * Math.pow(1 + r, n);
const denominator = Math.pow(1 + r, n) - 1;
const principalAndInterestPayment = P * (numerator / denominator);

// 3. Calculate total interest and total payments
const totalInterestDuringDraw = interestOnlyPayment * drawMonths;
const totalPaymentsDuringRepayment = principalAndInterestPayment * repaymentMonths;
const totalPayments = totalInterestDuringDraw + totalPaymentsDuringRepayment;
const totalInterestPaid = totalPayments - amountDrawn;

return {
  interestOnlyPayment: isNaN(interestOnlyPayment) ? 0 : interestOnlyPayment,
  principalAndInterestPayment: isNaN(principalAndInterestPayment) ? 0 : principalAndInterestPayment,
  totalInterestPaid: isNaN(totalInterestPaid) ? 0 : totalInterestPaid,
  totalPayments: isNaN(totalPayments) ? 0 : totalPayments
};
