function calculateBurnoutRisk(inputs) {
  const avgWeeklyHours = parseFloat(inputs.avgWeeklyHours) || 0;
  const overtimeHours = parseFloat(inputs.overtimeHours) || 0;
  const criticalIncidentExposure = parseFloat(inputs.criticalIncidentExposure) || 0;
  const dailyStressors = parseFloat(inputs.dailyStressors) || 0;
  const socialSupportScore = parseFloat(inputs.socialSupportScore) || 1; // Min score 1
  const organizationalSupportScore = parseFloat(inputs.organizationalSupportScore) || 1; // Min score 1
  const copingStrategiesScore = parseFloat(inputs.copingStrategiesScore) || 1; // Min score 1
  const sleepQualityScore = parseFloat(inputs.sleepQualityScore) || 1; // Min score 1
  const recoveryTimeRatio = parseFloat(inputs.recoveryTimeRatio) || 0; // Min score 0

  // Ensure scores are within valid ranges for calculation
  const clampedSocialSupport = Math.max(1, Math.min(5, socialSupportScore));
  const clampedOrganizationalSupport = Math.max(1, Math.min(5, organizationalSupportScore));
  const clampedCopingStrategies = Math.max(1, Math.min(5, copingStrategiesScore));
  const clampedSleepQuality = Math.max(1, Math.min(5, sleepQualityScore));
  const clampedRecoveryTimeRatio = Math.max(0, Math.min(1, recoveryTimeRatio));

  // --- Component Scores (Higher value = higher risk) ---

  // 1. Workload Score (W)
  // Base hours beyond 40 increase risk; overtime has a higher multiplier.
  let workloadRaw = Math.max(0, (avgWeeklyHours - 40) * 0.5) + (overtimeHours * 2);
  const W = Math.min(workloadRaw, 30); // Cap workload contribution at 30 points

  // 2. Exposure Score (E)
  // Critical incidents have a strong impact; daily stressors have a cumulative effect.
  let exposureRaw = (criticalIncidentExposure * 2) + (dailyStressors * 1);
  const E = Math.min(exposureRaw, 35); // Cap exposure contribution at 35 points

  // 3. Support Score (S) - This REDUCES risk, so it will be subtracted.
  // Higher support scores (5) mean less risk, lower scores (1) mean more risk.
  // We calculate 'lack of support' score: (5 - score) * multiplier
  let socialSupportImpact = (5 - clampedSocialSupport) * 2;
  let organizationalSupportImpact = (5 - clampedOrganizationalSupport) * 2;
  const S = Math.min(socialSupportImpact + organizationalSupportImpact, 16); // Max 16 points for lack of support

  // 4. Coping & Recovery Score (C) - This REDUCES risk, so it will be subtracted.
  // Higher coping/sleep scores (5) mean less risk; higher recovery ratio means less risk.
  // We calculate 'poor coping/recovery' score.
  let copingImpact = (5 - clampedCopingStrategies) * 2;
  let sleepImpact = (5 - clampedSleepQuality) * 2;
  let recoveryImpact = (1 - clampedRecoveryTimeRatio) * 20; // 0 recovery adds 20 points, 1 recovery adds 0.
  const C = Math.min(copingImpact + sleepImpact + recoveryImpact, 36); // Max 36 points for poor coping/recovery

  // --- Calculate Raw Burnout Risk Index ---
  // Start with a baseline risk common in high-stress professions.
  const baseRisk = 20;

  let rawBurnoutIndex = baseRisk + W + E + S + C; // S and C are already calculated as 'lack of' or 'poor' factors, thus they add to risk.

  // Normalize and Cap the final index to 0-100
  const burnoutRiskIndex = Math.max(0, Math.min(100, rawBurnoutIndex));

  // --- Determine Risk Level ---
  let riskLevel;
  if (burnoutRiskIndex <= 30) {
    riskLevel = 'Low Risk';
  } else if (burnoutRiskIndex <= 55) {
    riskLevel = 'Moderate Risk';
  } else if (burnoutRiskIndex <= 80) {
    riskLevel = 'High Risk';
  } else {
    riskLevel = 'Critical Risk';
  }

  return {
    burnoutRiskIndex: Math.round(burnoutRiskIndex),
    riskLevel: riskLevel
  };
}
