const buildingArea = inputs.buildingArea;
const buildingAge = inputs.buildingAge;
const projectComplexity = inputs.projectComplexity;
const materialFactor = inputs.materialFactor;
const laborRateFactor = inputs.laborRateFactor;
const contingency = inputs.contingency;

// Base costs per square foot (example values, highly variable by region/project)
const baseMaterialCostPerSqFt = 100; // E.g., for standard historic materials
const baseLaborCostPerSqFt = 120;   // E.g., for skilled historic preservation labor

// Project Complexity Multiplier (adjusts cost based on complexity level)
let complexityMultiplier;
if (projectComplexity === 1) complexityMultiplier = 1.0;
else if (projectComplexity === 2) complexityMultiplier = 1.25;
else if (projectComplexity === 3) complexityMultiplier = 1.5;
else if (projectComplexity === 4) complexityMultiplier = 1.8;
else if (projectComplexity === 5) complexityMultiplier = 2.2;
else complexityMultiplier = 1.0; // Default for unforeseen cases

// Building Age Multiplier
// Older buildings often require more delicate work, specialized techniques, and may uncover more hidden issues.
// This multiplier increases for buildings older than a certain threshold, up to a cap.
const ageThreshold = 50; // Years after which age starts impacting cost
const ageImpactPerYear = 0.005; // 0.5% increase per year over threshold
const maxAgeImpact = 0.8; // Capping the age impact to an 80% increase over base

let ageMultiplier = 1.0;
if (buildingAge > ageThreshold) {
    const yearsOverThreshold = buildingAge - ageThreshold;
    ageMultiplier = 1 + Math.min(maxAgeImpact, yearsOverThreshold * ageImpactPerYear);
}

// Calculate effective material cost per sq ft, considering complexity, age, and specialized materials
const effectiveMaterialCostPerSqFt = baseMaterialCostPerSqFt * complexityMultiplier * ageMultiplier * (1 + materialFactor / 100);

// Calculate effective labor cost per sq ft, considering complexity, age, and local rates
const effectiveLaborCostPerSqFt = baseLaborCostPerSqFt * complexityMultiplier * ageMultiplier * (1 + laborRateFactor / 100);

// Estimated Material Cost for the total area
const estimatedMaterialCost = effectiveMaterialCostPerSqFt * buildingArea;

// Estimated Labor Cost for the total area
const estimatedLaborCost = effectiveLaborCostPerSqFt * buildingArea;

// Subtotal before contingency fund
const subtotalCost = estimatedMaterialCost + estimatedLaborCost;

// Total Project Cost including contingency for unforeseen expenses
const totalProjectCost = subtotalCost * (1 + contingency / 100);

return {
    estimatedMaterialCost: estimatedMaterialCost,
    estimatedLaborCost: estimatedLaborCost,
    totalProjectCost: totalProjectCost
};
