function calculate({
  distance_km,
  num_travelers,
  transport_mode,
  car_fuel_efficiency_lp100km,
  car_fuel_type,
  flight_class
}) {
  let totalCO2_kg = 0;
  let co2_per_traveler_kg = 0;

  // Emission factors and constants
  const GASOLINE_EMISSION_FACTOR_KG_CO2_PER_L = 2.31;
  const DIESEL_EMISSION_FACTOR_KG_CO2_PER_L = 2.68;
  // Electric cars have 0 tailpipe emissions. Upstream depends on grid, but for direct travel, we consider 0.
  const ELECTRIC_EMISSION_FACTOR_KG_CO2_PER_L = 0;

  const PLANE_SHORT_HAUL_EMISSION_FACTOR_KG_CO2_PER_PKM = 0.25; // < 1000 km
  const PLANE_LONG_HAUL_EMISSION_FACTOR_KG_CO2_PER_PKM = 0.15;  // > 1000 km
  const PLANE_RFI_MULTIPLIER = 2.0; // Radiative Forcing Index for non-CO2 effects at altitude

  const TRAIN_EMISSION_FACTOR_KG_CO2_PER_PKM = 0.04;
  const BUS_EMISSION_FACTOR_KG_CO2_PER_PKM = 0.10;

  const TREE_ABSORPTION_KG_CO2_PER_YEAR = 22; // Average CO2 absorption by a mature tree per year

  // Input validation and normalization
  distance_km = Math.max(0, parseFloat(distance_km) || 0);
  num_travelers = Math.max(1, parseInt(num_travelers) || 1);
  transport_mode = (transport_mode || '').toLowerCase().trim();
  car_fuel_type = (car_fuel_type || '').toLowerCase().trim();
  flight_class = (flight_class || '').toLowerCase().trim();

  if (distance_km === 0) {
    return { total_co2_kg: 0, co2_per_traveler_kg: 0, offset_trees_years: 0 };
  }

  switch (transport_mode) {
    case 'car':
      if (car_fuel_efficiency_lp100km && car_fuel_efficiency_lp100km > 0) {
        const fuel_consumed_L = (distance_km / 100) * car_fuel_efficiency_lp100km;
        let emission_factor;
        if (car_fuel_type === 'gasoline') {
          emission_factor = GASOLINE_EMISSION_FACTOR_KG_CO2_PER_L;
        } else if (car_fuel_type === 'diesel') {
          emission_factor = DIESEL_EMISSION_FACTOR_KG_CO2_PER_L;
        } else if (car_fuel_type === 'electric') {
          emission_factor = ELECTRIC_EMISSION_FACTOR_KG_CO2_PER_L;
        } else {
          // Default to gasoline if unknown or no fuel type provided
          emission_factor = GASOLINE_EMISSION_FACTOR_KG_CO2_PER_L;
        }
        totalCO2_kg = fuel_consumed_L * emission_factor;
      } else {
        // Fallback for car with no efficiency provided (e.g., assume average gasoline car)
        const avg_efficiency_lp100km = 7.5; // Example average
        const fuel_consumed_L = (distance_km / 100) * avg_efficiency_lp100km;
        totalCO2_kg = fuel_consumed_L * GASOLINE_EMISSION_FACTOR_KG_CO2_PER_L;
      }
      // Car emissions are usually shared, but for simplicity, we attribute total to the travel party.
      // If `num_travelers` represents unique vehicles, this would change.
      // Assuming `num_travelers` share one vehicle for `totalCO2_kg` and then divide for `per_traveler`.
      break;

    case 'plane':
      let plane_emission_factor;
      if (distance_km <= 1000) {
        plane_emission_factor = PLANE_SHORT_HAUL_EMISSION_FACTOR_KG_CO2_PER_PKM;
      } else {
        plane_emission_factor = PLANE_LONG_HAUL_EMISSION_FACTOR_KG_CO2_PER_PKM;
      }

      let class_multiplier = 1.0;
      if (flight_class === 'business') {
        class_multiplier = 2.5;
      } else if (flight_class === 'first') {
        class_multiplier = 4.0;
      }

      totalCO2_kg = distance_km * num_travelers * plane_emission_factor * class_multiplier * PLANE_RFI_MULTIPLIER;
      break;

    case 'train':
      totalCO2_kg = distance_km * num_travelers * TRAIN_EMISSION_FACTOR_KG_CO2_PER_PKM;
      break;

    case 'bus':
      totalCO2_kg = distance_km * num_travelers * BUS_EMISSION_FACTOR_KG_CO2_PER_PKM;
      break;

    default:
      // Unrecognized mode, or default to a high-emission mode as a warning
      // For calculation, let's treat as 0 or provide a warning via UI. Here, we'll return 0 for default/unknown.
      totalCO2_kg = 0;
      break;
  }

  co2_per_traveler_kg = num_travelers > 0 ? totalCO2_kg / num_travelers : 0;
  const offset_trees_years = totalCO2_kg > 0 ? Math.max(0, Math.ceil(totalCO2_kg / TREE_ABSORPTION_KG_CO2_PER_YEAR)) : 0;

  return {
    total_co2_kg: totalCO2_kg,
    co2_per_traveler_kg: co2_per_traveler_kg,
    offset_trees_years: offset_trees_years
  };
}
