function calculate({ total_appliance_watts, backup_duration_hours, battery_voltage, depth_of_discharge, inverter_efficiency, system_losses, surge_factor }) {
  // Ensure inputs are valid numbers and handle defaults for optional fields
  const load_watts = Math.max(0, parseFloat(total_appliance_watts) || 0);
  const duration_hours = Math.max(0, parseFloat(backup_duration_hours) || 0);
  const bat_voltage = Math.max(1, parseFloat(battery_voltage) || 1); // Prevent division by zero
  const dod = Math.max(1, Math.min(100, parseFloat(depth_of_discharge) || 80)); // Cap DoD between 1 and 100
  const inv_eff = Math.max(1, Math.min(100, parseFloat(inverter_efficiency) || 90)); // Cap efficiency between 1 and 100
  const sys_losses = Math.max(0, Math.min(99, parseFloat(system_losses) || 5)); // Cap losses between 0 and 99
  const s_factor = Math.max(0, parseFloat(surge_factor) || 20);

  // Convert percentages to decimals
  const dod_decimal = dod / 100;
  const inverter_eff_decimal = inv_eff / 100;
  const system_efficiency_decimal = (100 - sys_losses) / 100;
  const surge_factor_decimal = s_factor / 100;

  // 1. Calculate total energy needed in Watt-hours (Wh) from appliances
  const total_energy_wh_raw = load_watts * duration_hours;

  // 2. Adjust for inverter efficiency and general system losses
  // We need MORE energy from the battery than what the appliances consume due to inefficiencies
  const energy_wh_after_losses = total_energy_wh_raw / (inverter_eff_decimal * system_efficiency_decimal);

  // 3. Adjust for Usable Depth of Discharge (DoD)
  // The battery's full rated capacity must be larger than the usable energy to respect DoD limits
  const required_full_battery_wh = energy_wh_after_losses / dod_decimal;

  // 4. Convert to kWh
  const required_battery_capacity_kwh = required_full_battery_wh / 1000;

  // 5. Calculate Amp-hours (Ah) - based on the full battery Wh capacity
  const required_battery_capacity_ah = required_full_battery_wh / bat_voltage;

  // 6. Calculate Recommended Inverter Size
  // Inverter must handle the total appliance load plus a surge factor for startup currents
  const recommended_inverter_size_watts = load_watts * (1 + surge_factor_decimal);

  return {
    required_battery_capacity_kwh: Math.max(0, required_battery_capacity_kwh),
    required_battery_capacity_ah: Math.max(0, required_battery_capacity_ah),
    recommended_inverter_size_watts: Math.max(0, recommended_inverter_size_watts)
  };
}
