function calculate({ original_gravity, final_gravity, boil_volume_start, boil_volume_end, hop_weight_grams, hop_alpha_acid, hop_boil_time_minutes, wort_pre_boil_gravity }) {
    let abv = 0;
    let ibu = 0;
    let boil_off_volume_liters = 0;
    let boil_off_percentage = 0;

    // Validate inputs to prevent division by zero or negative results
    if (original_gravity <= 0 || final_gravity <= 0 || boil_volume_start <= 0 || boil_volume_end <= 0 || hop_weight_grams < 0 || hop_alpha_acid < 0 || hop_boil_time_minutes < 0 || wort_pre_boil_gravity <= 0) {
        return { abv: NaN, ibu: NaN, boil_off_volume_liters: NaN, boil_off_percentage: NaN };
    }

    // Ensure sensible gravity values for ABV calculation
    if (original_gravity < final_gravity) {
        abv = 0; // Fermentation did not occur or was negative, ABV is 0
    } else {
        // ABV Calculation: (OG - FG) * 131.25 (standard approximation for OG/FG in SG)
        abv = (original_gravity - final_gravity) * 131.25;
    }

    // Boil-Off Calculation
    boil_off_volume_liters = Math.max(0, boil_volume_start - boil_volume_end);
    if (boil_volume_start > 0) {
        boil_off_percentage = (boil_off_volume_liters / boil_volume_start) * 100;
    } else {
        boil_off_percentage = 0;
    }

    // IBU Calculation (Tinseth Formula)
    // Ensure boil_volume_end is greater than 0 for IBU calculation and other hop parameters are valid
    if (boil_volume_end > 0 && hop_alpha_acid > 0 && hop_weight_grams > 0) {
        const alpha_acid_decimal = hop_alpha_acid / 100; // Convert percentage (e.g., 6.5) to decimal (0.065)

        // Gravity Factor (Fg) - Tinseth: accounts for reduced utilization in higher gravity wort
        // Formula: 1.65 * 0.000125^(Wort Gravity - 1)
        const gravity_factor = 1.65 * Math.pow(0.000125, (wort_pre_boil_gravity - 1));

        // Boil Time Factor (Fb) - Tinseth: accounts for isomerization over time
        // Formula: (1 - Math.exp(-0.04 * Boil Time)) / 4.15
        const boil_time_factor = (1 - Math.exp(-0.04 * hop_boil_time_minutes)) / 4.15;

        // Combined Utilization Factor (U)
        let utilization = gravity_factor * boil_time_factor;
        utilization = Math.max(0, Math.min(utilization, 0.35)); // Cap utilization at a realistic maximum, e.g., 35-40%

        // IBU Formula: (Hop Weight (g) * Alpha Acid (decimal) * Utilization * 10) / Final Wort Volume (L)
        // The factor of 10 converts grams to milligrams (1000) and then scales for the alpha acid being a decimal (dividing by 100).
        // So, 1000 * (1/100) = 10, to get mg/L (IBU) from g * decimal_AA / L.
        ibu = (hop_weight_grams * alpha_acid_decimal * utilization * 10) / boil_volume_end;
    }

    // Cap ABV and IBU at reasonable maximums in case of extreme inputs for realism
    abv = Math.min(Math.max(0, abv), 25); // Cap ABV between 0% and 25%
    ibu = Math.min(Math.max(0, ibu), 150); // Cap IBU between 0 and 150

    return {
        abv: parseFloat(abv.toFixed(2)),
        ibu: parseFloat(ibu.toFixed(1)),
        boil_off_volume_liters: parseFloat(boil_off_volume_liters.toFixed(2)),
        boil_off_percentage: parseFloat(boil_off_percentage.toFixed(2))
    };
}
