const desiredMashTemp = inputs.desiredMashTemp; 
const grainWeight = inputs.grainWeight; 
const grainTemp = inputs.grainTemp; 
const mashThickness = inputs.mashThickness; 
const mashTunThermalMass = inputs.mashTunThermalMass || 0; 
const systemHeatLoss = inputs.systemHeatLoss || 0; 

const CP_WATER = 1; // BTU/lb/°F 
const CP_GRAIN = 0.38; // BTU/lb/°F (Approximate for malt) 
const WATER_DENSITY_LBS_PER_GAL = 8.34; // lbs/gallon 
const QUARTS_PER_GALLON = 4; 

// 1. Calculate Strike Water Volume 
const strikeWaterVolume_qts = grainWeight * mashThickness; 
const strikeWaterVolume_gallons = strikeWaterVolume_qts / QUARTS_PER_GALLON; 
const strikeWaterVolume_lbs = strikeWaterVolume_gallons * WATER_DENSITY_LBS_PER_GAL; 

// 2. Calculate Heat required to raise grains to desired mash temp 
const heatForGrains = grainWeight * CP_GRAIN * (desiredMashTemp - grainTemp); 

// 3. Calculate Heat required to raise mash tun to desired mash temp 
const heatForMashTun = mashTunThermalMass * CP_WATER * (desiredMashTemp - grainTemp); 

// 4. Total heat needed from strike water 
const totalHeatNeeded = heatForGrains + heatForMashTun; 

// 5. Calculate Strike Water Temperature (initial, without heat loss) 
// Q_water = strikeWaterVolume_lbs * CP_WATER * (strikeWaterTemp - desiredMashTemp) 
// strikeWaterTemp = desiredMashTemp + (totalHeatNeeded / (strikeWaterVolume_lbs * CP_WATER)) 
let strikeWaterTemp = desiredMashTemp + (totalHeatNeeded / (strikeWaterVolume_lbs * CP_WATER)); 

// 6. Apply System Heat Loss 
// If there's a heat loss, the strike water needs to be hotter to compensate. 
// We effectively need to achieve the target heat *after* the loss. 
// So, if X is the calculated temp difference from desiredMashTemp, and Y is heat loss %, 
// we need to add X * (1 + Y/100) to desiredMashTemp. 
if (systemHeatLoss > 0) { 
    const tempDifferenceFromMash = strikeWaterTemp - desiredMashTemp; 
    strikeWaterTemp = desiredMashTemp + (tempDifferenceFromMash * (1 + systemHeatLoss / 100)); 
} 

// Cap strike water temperature to reasonable brewing range 
strikeWaterTemp = Math.max(100, Math.min(212, strikeWaterTemp)); // Between 100F and 212F 

return { 
  strikeWaterTemp: strikeWaterTemp, 
  strikeWaterVolume: strikeWaterVolume_gallons 
};
