const P_propertyValue = Math.max(0, propertyValue || 0);
const P_preparednessCost = Math.max(0, preparednessCost || 0);
const P_seismicRiskLevel = Math.min(10, Math.max(1, seismicRiskLevel || 1));
const P_potentialUnmitigatedDamagePercentage = Math.min(100, Math.max(0, potentialUnmitigatedDamagePercentage || 0));
const P_damageReductionEfficiency = Math.min(100, Math.max(0, damageReductionEfficiency || 0));
const P_earthquakeInsuranceDeductible = Math.max(0, earthquakeInsuranceDeductible || 0);
const P_earthquakeInsuranceCoverageLimit = Math.max(0, earthquakeInsuranceCoverageLimit || 0);
const P_riskAssessmentTimeHorizon = Math.max(1, riskAssessmentTimeHorizon || 1);

let netFinancialBenefit = 0;
let roiPercentage = 0;
let paybackPeriodYears = Infinity;

// Handle edge case where property value is zero or inputs are too low to compute meaningful results
if (P_propertyValue === 0 || P_riskAssessmentTimeHorizon === 0) {
    return { netFinancialBenefit: 0, roiPercentage: 0, paybackPeriodYears: Infinity };
}

// Step 1: Calculate potential damage value without preparedness measures
const unmitigatedDamageValue = P_propertyValue * (P_potentialUnmitigatedDamagePercentage / 100);

// Step 2: Calculate potential damage value with preparedness measures
const mitigatedDamageValue = unmitigatedDamageValue * (1 - (P_damageReductionEfficiency / 100));

// Step 3: Calculate out-of-pocket loss without preparedness, considering insurance (per event)
const actualDamageUnmitigated = unmitigatedDamageValue;
const insuredLossUnmitigated = Math.max(0, actualDamageUnmitigated - P_earthquakeInsuranceDeductible);
const insurancePayoutUnmitigated = Math.min(insuredLossUnmitigated, P_earthquakeInsuranceCoverageLimit);
const yourOutOfPocketLossUnmitigated = actualDamageUnmitigated - insurancePayoutUnmitigated;

// Step 4: Calculate out-of-pocket loss with preparedness, considering insurance (per event)
const actualDamageMitigated = mitigatedDamageValue;
const insuredLossMitigated = Math.max(0, actualDamageMitigated - P_earthquakeInsuranceDeductible);
const insurancePayoutMitigated = Math.min(insuredLossMitigated, P_earthquakeInsuranceCoverageLimit);
const yourOutOfPocketLossMitigated = actualDamageMitigated - insurancePayoutMitigated;

// Step 5: Calculate gross financial benefit from preparedness per event
// This is the reduction in your out-of-pocket loss due to preparedness
const grossBenefitPerEvent = Math.max(0, yourOutOfPocketLossUnmitigated - yourOutOfPocketLossMitigated);

// Step 6: Determine the probability of a damaging earthquake occurring within the time horizon
// Simplified model: seismicRiskLevel (1-10) maps to a probability (10% to 100%) for the entire time horizon.
// This means a level 1 risk has 10% chance of a damaging event, level 10 has 100% chance over the horizon.
const eventProbabilityOverHorizon = P_seismicRiskLevel / 10;

// Step 7: Calculate total expected financial benefit over the risk assessment time horizon
// This accounts for the likelihood of an earthquake occurring.
const totalExpectedBenefit = grossBenefitPerEvent * eventProbabilityOverHorizon;

// Step 8: Calculate Net Financial Benefit
netFinancialBenefit = totalExpectedBenefit - P_preparednessCost;

// Step 9: Calculate Return on Investment (ROI)
if (P_preparednessCost === 0) {
    roiPercentage = totalExpectedBenefit > 0 ? Infinity : 0;
} else {
    roiPercentage = (netFinancialBenefit / P_preparednessCost) * 100;
}

// Step 10: Calculate Payback Period
// Annualized probability of a significant event is derived from the overall probability over the time horizon.
const annualEventProbability = eventProbabilityOverHorizon / P_riskAssessmentTimeHorizon;
const annualExpectedBenefit = grossBenefitPerEvent * annualEventProbability;

if (annualExpectedBenefit > 0) {
    paybackPeriodYears = P_preparednessCost / annualExpectedBenefit;
    // If the expected benefit is less than cost, or ROI is negative, payback is effectively infinite
    if (paybackPeriodYears < 0 || netFinancialBenefit < 0) {
        paybackPeriodYears = Infinity;
    }
} else {
    paybackPeriodYears = Infinity;
}

// Ensure output values are finite numbers or Infinity as appropriate
roiPercentage = Number.isFinite(roiPercentage) ? roiPercentage : (roiPercentage > 0 ? Infinity : -Infinity);
paybackPeriodYears = Number.isFinite(paybackPeriodYears) ? paybackPeriodYears : Infinity;

return {
    netFinancialBenefit: netFinancialBenefit,
    roiPercentage: roiPercentage,
    paybackPeriodYears: paybackPeriodYears
};
