const WATER_GALLONS_PER_PERSON_PER_DAY = 1;
const CALORIES_PER_PERSON_PER_DAY = 2000;
const FOOD_WEIGHT_LBS_PER_1000_CALORIES = 0.5; // Approximation for dry goods/MREs
const WATER_WEIGHT_LBS_PER_GALLON = 8.34;

const basicAidCost = 25;
const standardAidCost = 75;
const advancedAidCost = 150;

const basicAidWeight = 1;
const standardAidWeight = 3;
const advancedAidWeight = 6;

// Calculate costs
let foodCost = householdSize * durationDays * foodBudgetPerPersonPerDay;
let waterCost = householdSize * durationDays * waterBudgetPerPersonPerDay;

let firstAidCost = 0;
if (firstAidKitLevel === 0) {
  firstAidCost = basicAidCost;
} else if (firstAidKitLevel === 1) {
  firstAidCost = standardAidCost;
} else if (firstAidKitLevel === 2) {
  firstAidCost = advancedAidCost;
}

let subtotalCost = foodCost + waterCost + initialFixedCosts + firstAidCost;
let specialNeedsCostAddition = subtotalCost * (specialNeedsFactor / 100);
let estimatedTotalCost = subtotalCost + specialNeedsCostAddition;

// Calculate water and food quantities
let totalWaterNeededGallons = householdSize * durationDays * WATER_GALLONS_PER_PERSON_PER_DAY;
let minFoodCaloriesTotal = householdSize * durationDays * CALORIES_PER_PERSON_PER_DAY;

// Calculate estimated weight
let foodWeightLbs = (minFoodCaloriesTotal / 1000) * FOOD_WEIGHT_LBS_PER_1000_CALORIES;
let waterWeightLbs = totalWaterNeededGallons * WATER_WEIGHT_LBS_PER_GALLON;

let firstAidWeight = 0;
if (firstAidKitLevel === 0) {
  firstAidWeight = basicAidWeight;
} else if (firstAidKitLevel === 1) {
  firstAidWeight = standardAidWeight;
} else if (firstAidKitLevel === 2) {
  firstAidWeight = advancedAidWeight;
}

// Rough estimate for fixed equipment weight (e.g., $100 equipment ~ 10 lbs, capped)
let initialEquipmentWeightLbs = Math.min(initialFixedCosts * 0.1, 50);

let estimatedKitWeightLbs = foodWeightLbs + waterWeightLbs + firstAidWeight + initialEquipmentWeightLbs;

// Cap total weight to prevent absurd numbers from extreme inputs
estimatedKitWeightLbs = Math.min(estimatedKitWeightLbs, 500);

return {
  estimatedTotalCost: estimatedTotalCost,
  totalWaterNeededGallons: totalWaterNeededGallons,
  minFoodCaloriesTotal: minFoodCaloriesTotal,
  estimatedKitWeightLbs: estimatedKitWeightLbs
};
