function (inputs) {
  const currentElectricityCostTotal = inputs.currentAnnualElectricity_kWh * inputs.currentElectricityCost_per_kWh;
  const currentNaturalGasCostTotal = inputs.currentAnnualNaturalGas_therms * inputs.currentNaturalGasCost_per_therm;

  const annualElectricitySavings = currentElectricityCostTotal * (inputs.projectedElectricityReduction_percent / 100);
  const annualNaturalGasSavings = currentNaturalGasCostTotal * (inputs.projectedNaturalGasReduction_percent / 100);
  const totalAnnualSavings = annualElectricitySavings + annualNaturalGasSavings;

  const netInvestment = inputs.totalInstallationCost - inputs.availableIncentives;

  let paybackPeriod_years;
  if (totalAnnualSavings <= 0) {
    paybackPeriod_years = Infinity; // No savings, no payback
  } else if (netInvestment <= 0) {
    paybackPeriod_years = 0; // Investment covered by incentives or negative, instant payback
  } else {
    paybackPeriod_years = netInvestment / totalAnnualSavings;
  }

  let roi_firstYear, roi_fiveYears, roi_tenYears;

  if (netInvestment <= 0) {
    // If net investment is 0 or negative, ROI is effectively infinite or an instant, complete return.
    // For practical reporting, we can use a very high percentage to denote this.
    roi_firstYear = 9999;
    roi_fiveYears = 9999;
    roi_tenYears = 9999;
  } else if (totalAnnualSavings <= 0) {
    // If there are no savings but there is an investment, ROI is -100% as the entire investment is lost.
    roi_firstYear = -100;
    roi_fiveYears = -100;
    roi_tenYears = -100;
  } else {
    roi_firstYear = (totalAnnualSavings / netInvestment) * 100;
    roi_fiveYears = ((totalAnnualSavings * 5 - netInvestment) / netInvestment) * 100;
    roi_tenYears = ((totalAnnualSavings * 10 - netInvestment) / netInvestment) * 100;
  }

  return {
    annualElectricitySavings: annualElectricitySavings,
    annualNaturalGasSavings: annualNaturalGasSavings,
    totalAnnualSavings: totalAnnualSavings,
    netInvestment: netInvestment,
    paybackPeriod_years: paybackPeriod_years,
    roi_firstYear: roi_firstYear,
    roi_fiveYears: roi_fiveYears,
    roi_tenYears: roi_tenYears
  };
}
